a!localVariables(
  local!availableCaseFieldsCustomizations: rule!CMGT_AUTO_EVAL_AvailableFieldsCustomizationsWrapper_Case(),
  local!availableTaskFieldsCustomizations: rule!CMGT_AUTO_EVAL_AvailableFieldsCustomizationsWrapper_Task(),
  local!studioParentRecordTypeUuid: a!defaultValue(
    ri!caseTypeRecordTypeUuid,
    ri!parentCaseTypeRecordTypeUuid
  ),
  local!caseFields: append(
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        local!availableCaseFieldsCustomizations.fieldMapsToAdd
      )
    ),
    a!flatten(
      local!availableCaseFieldsCustomizations.customFieldsToAdd
    )
  ),
  local!taskFields: if(
    ri!scope = "TASK",
    append(
      rule!CMGT_UTIL_RejectBlank(
        a!flatten(
          local!availableTaskFieldsCustomizations.fieldMapsToAdd
        )
      ),
      a!flatten(
        local!availableTaskFieldsCustomizations.customFieldsToAdd
      )
    ),
    null()
  ),
  local!controlPanelHierarchy: #"SYSTEM_SYSRULES_controlPanelRecordHierarchyMetadata"(
    controlPanel: #"urn:appian:control-panel:v1:1b82dc2b-d4df-44ed-ac7c-3cce3b4ee0e0",
    recordUuid: local!studioParentRecordTypeUuid
  ),
  local!studioFields: rule!CMGT_formatStudioFieldHierarchy(
    controlPanelHierarchy: local!controlPanelHierarchy
  ),
  local!allFields: rule!CMGT_UTIL_RejectBlank(
    a!flatten(
      {
        local!studioFields,
        local!caseFields,
        local!taskFields
      }
    )
  ),
  local!containsDuplicateNames: count(local!allFields.displayName) <> count(
    rule!CMGT_UTIL_Distinct(local!allFields.displayName)
  ),
  local!formatFieldLabels: #"SYSTEM_SYSRULES_forEach"(
    items: local!allFields,
    expression: #"urn:appian:function:v1:a:update"(
      data: fv!item,
      index: "label",
      value: if(
        and(
          local!containsDuplicateNames,
          /*Determines is the field is a duplicate   */
          count(
            wherecontains(
              fv!item.displayName,
              local!allFields.displayName
            )
          ) > 1
        ),
        a!match(
          value: fv!item.parentRecordTypeUuid,
          equals: cons!CMGT_UUID_RECORD_TYPE_CASE,
          then: concat("Case ", fv!item.displayName),
          equals: cons!CMGT_UUID_RECORD_TYPE_TASK,
          then: concat("Task ", fv!item.displayName),
          default: fv!item.displayName,

        ),
        fv!item.displayName
      )
    )
  ),
  local!formatFieldLabels
)

