  taskIntentionCodes: {cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER},
  fields: {
    recordType!CMGT_Task.taskInstanceUuid,
    recordType!CMGT_Task.taskIntentionCode,
    recordType!CMGT_Task.automationTaskAction,
    recordType!CMGT_Task.automationTaskAction,
    #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/45ed19eb-2545-4c96-9e19-72127f9d27a3/dc83b26a-0034-46f4-8085-5887ad59bc08/86c11a87-1ab5-4e62-93f2-b7d24e412b3a",
    recordType!CMGT_Task.conditionSet,
    recordType!CMGT_Task.automationTrigger,
    recordType!CMGT_Task.triggerContext,
    recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid
  },
  additionalLogicalExpression: {
    #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      filters: {
      },
      logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        logicalExpressions: {
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
                operator: "=",
                value: ri!task[recordType!CMGT_Task.taskInstanceUuid],
              )
            }
          ),
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            logicalExpressions: {
              /*filters on rules where the trigger does not need any context -TODO*/
              /*filters on rules where the context is needed*/
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_Task.automationTrigger.triggerId,
                    operator: "in",
                    value: ri!triggerIds,
                    applyWhen: contains(ri!triggerIds, cons!CMGT_REFID_TRIGGER_TASK_FIELD_IS_UPDATED)
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_Task.triggerContext,
                    operator: "in",
                    value: ri!triggerContext,
                    applyWhen: contains(ri!triggerIds, cons!CMGT_REFID_TRIGGER_TASK_FIELD_IS_UPDATED)
                  )
                }
              )
            },
            filters: {}
          )
        }
      )
    )
  },
  sort: {
    #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!CMGT_Task.sortOrder,
      ascending: true
    )
  }
)

