_a-0000e9a5-4cb8-8000-9bac-011c48011c48_.sail_code:
a!localVariables(
  local!brandingMap: rule!CMGT_UTIL_LoadBrandingMap(),
  local!loggedInUser: loggedInUser(),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION"
  ),
  local!caseAssigneeLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.roles.userAssignee,
        operator: "=",
        value: local!loggedInUser
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.statusId,
        operator: "<>",
        value: cons!CMGT_REFID_STATUS_CASE_CLOSED
      )
    }
  ),
  local!recentCases: rule!CMGT_QR_GetCase(
    additionalLogicalExpression: local!caseAssigneeLogicalExpression,
    fields: {
      recordType!CMGT_Case.status,
      recordType!CMGT_Case.priority,
      recordType!CMGT_Case.createdByUser
    },
    includeAssignees: true,
    includePrimaryContact: true,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_INT_BATCH_SIZE_CARD_SMALL,
      sort: {
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!CMGT_Case.dueOnSortOrder,
          ascending: true
        ),
        #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!CMGT_Case.dueOn,
          ascending: true
        )
      }
    )
  ),
  local!businessGroups: a!refreshVariable(
    value: a!groupMembers(
      group: cons!CMGT_GRP_BUSINESS_GROUPS,
      memberType: "GROUP"
    ).data,
    refreshOnReferencedVarChange: false
  ),
  local!userGroupsToFilter: intersection(
    togroup(local!businessGroups),
    a!groupsForUser(loggedInUser())
  ),
  local!taskTabs: rule!CMGT_Task_EvaluateDataForDisplay(userGroups: local!userGroupsToFilter),
  local!selectedTaskTab: 1,
  local!metrics,
  local!phone: { "PHONE" },
  local!isPhone: #"SYSTEM_SYSRULES_isPageWidth"(local!phone),
  #"SYSTEM_SYSRULES_headerContentLayout"(
    header: {},
    contents: {
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: {
          #"SYSTEM_SYSRULES_columnsLayout"(
            columns: {
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: {
                  /*Quick Links*/
                  rule!CMGT_Workspace_InternalUser_Sub_QuickLinks(brandingMap: local!brandingMap),
                  /*Latest Updates*/
                  rule!CMGT_UTIL_HeaderField(
                    label: #"urn:appian:translation-string:v1:2d969cfc-8acd-48ff-b6a7-46cd4b6e90dc",
                    divider: "NONE",
                    brandingMap: local!brandingMap,
                    marginBelow: "NONE"
                  ),
                  rule!CMGT_EventHistory_Sub_LatestUpdates(
                    brandingMap: local!brandingMap,
                    isPhone: local!isPhone
                  )
                },
                width: "NARROW_PLUS"
              ),
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: #"SYSTEM_SYSRULES_columnsLayout"(
                  columns: {
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        /*My Cases*/
                        rule!CMGT_UTIL_HeaderField(
                          label: #"urn:appian:translation-string:v1:25a60250-ed07-4581-9e56-1e84702adee8",
                          divider: "NONE",
                          rightSideContents: {
                            #"SYSTEM_SYSRULES_sideBySideLayout"(
                              items: {
                                #"SYSTEM_SYSRULES_sideBySideItem"(),
                                #"SYSTEM_SYSRULES_sideBySideItem"(
                                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                                    labelPosition: "COLLAPSED",
                                    value: {
                                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                                        text: #"urn:appian:translation-string:v1:5d80b586-40a7-4438-aabb-e6c7e5d19122",
                                        link: #"SYSTEM_SYSRULES_safeLink"(
                                          uri: a!urlForSite(sitePage: rule!CMGT_SITE_PAGE_CASES()),
                                          openLinkIn: "SAME_TAB"
                                        ),
                                        linkStyle: "STANDALONE"
                                      )
                                    },
                                    align: "RIGHT"
                                  ),
                                  width: "MINIMIZE"
                                )
                              }
                            )
                          },
                          brandingMap: local!brandingMap,
                          marginBelow: "NONE"
                        ),
                        if(
                          a!isNullOrEmpty(local!recentCases),
                          #"SYSTEM_SYSRULES_cardLayout"(
                            contents: {
                              #"SYSTEM_SYSRULES_richTextDisplayField"(
                                value: {
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                                    text: #"urn:appian:translation-string:v1:e0a8b2dd-4ac9-463f-9d0a-a591599ae3c3",
                                    color: local!brandingMap.SecondaryTextColor,
                                    style: "EMPHASIS"
                                  )
                                },
                                align: "CENTER"
                              )
                            },
                            style: local!brandingMap.CardBackgroundColor,
                            shape: local!brandingMap.CardShape,
                            padding: "EVEN_MORE",
                            marginBelow: "STANDARD",
                            showBorder: local!brandingMap.CardShowBorder,
                            showShadow: local!brandingMap.CardShowShadow
                          ),
                          rule!CMGT_UTIL_CardGroupHandler(
                            maxPerRow: if(
                              #"SYSTEM_SYSRULES_isPageWidth"({ "DESKTOP_NARROW", "TABLET_PORTRAIT" }),
                              2,
                              if(local!isPhone, 1, 3)
                            ),
                            cardLayouts: {
                              #"SYSTEM_SYSRULES_forEach"(
                                items: local!recentCases,
                                expression: #"SYSTEM_SYSRULES_cardLayout"(
                                  contents: {
                                    rule!CMGT_Case_Sub_DetailsSmall(
                                      case: fv!item,
                                      showRecordLink: false,
                                      brandingMap: local!brandingMap
                                    )
                                  },
                                  link: #"SYSTEM_SYSRULES_recordLink"(
                                    recordType: recordType!CMGT_Case,
                                    identifier: fv!item[recordType!CMGT_Case.caseId]
                                  ),
                                  style: local!brandingMap.CardBackgroundColor,
                                  shape: local!brandingMap.CardShape,
                                  showBorder: local!brandingMap.CardShowBorder,
                                  showShadow: local!brandingMap.CardShowShadow
                                )
                              )
                            }
                          )
                        ),
                        /*My Tasks*/
                        rule!CMGT_UTIL_HeaderField(
                          label: #"urn:appian:translation-string:v1:1411ee2d-e580-4a80-8cfc-5fa873b9ec86",
                          divider: "NONE",
                          brandingMap: local!brandingMap,
                          marginBelow: "NONE"
                        ),
                        #"SYSTEM_SYSRULES_cardLayout"(
                          contents: {
                            rule!CMGT_UTIL_TabLayout(
                              tabs: local!taskTabs,
                              activeTab: local!selectedTaskTab,
                              brandingMap: local!brandingMap,
                              metrics: local!metrics
                            ),
                            #"SYSTEM_SYSRULES_cardLayout"(
                              contents: {
                                rule!CMGT_Task_RecordGrid(
                                  logicalExpression: local!taskTabs[local!selectedTaskTab].taskLogicalExpression,
                                  label: #"urn:appian:translation-string:v1:1411ee2d-e580-4a80-8cfc-5fa873b9ec86",
                                  showFilters: not(local!selectedTaskTab = 1),
                                  showSearchBox: true,
                                  showStatusFilter: false,
                                  showAssigneeFilter: true,
                                  showRefresh: false(),
                                  pageSize: cons!CMGT_INT_BATCH_SIZE_LARGE,
                                  borderStyle: "LIGHT",
                                  triggerRefresh: local!triggerRefresh,
                                  brandingMap: local!brandingMap,
                                  initialSorts: {
                                    #"SYSTEM_SYSRULES_sortInfo"(
                                      field: recordType!CMGT_Task.status.sortOrder,
                                      ascending: true
                                    ),
                                    #"SYSTEM_SYSRULES_sortInfo"(
                                      field: recordType!CMGT_Task.dueOnSortOrder,
                                      ascending: true
                                    ),
                                    #"SYSTEM_SYSRULES_sortInfo"(
                                      field: recordType!CMGT_Task.dueOn,
                                      ascending: true
                                    )
                                  },
                                  context: if(
                                    local!selectedTaskTab = 1,
                                    "MY_TASK_VIEW",
                                    "MY_SHARED_QUEUE"
                                  )
                                )
                              },
                              showBorder: false,
                              style: "TRANSPARENT"
                            )
                          },
                          style: local!brandingMap.CardBackgroundColor,
                          shape: local!brandingMap.CardShape,
                          padding: "NONE",
                          showBorder: local!brandingMap.CardShowBorder,
                          showShadow: local!brandingMap.CardShowShadow
                        )
                      },
                      width: "AUTO"
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        /*My Work In Progress*/
                        rule!CMGT_Workspace_InternalUser_Sub_MyWorkInProgress(
                          triggerRefresh: local!triggerRefresh,
                          loggedInUser: local!loggedInUser,
                          brandingMap: local!brandingMap
                        ),
                        /*Calendar*/
                        rule!CMGT_Workspace_InternalUser_Sub_CalendarDisplay(
                          loggedInUser: local!loggedInUser,
                          brandingMap: local!brandingMap
                        )
                      },
                      width: "NARROW_PLUS"
                    )
                  },
                  stackWhen: {
                    "TABLET_LANDSCAPE",
                    "TABLET_PORTRAIT",
                    local!phone
                  }
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                #"urn:appian:translation-string:v1:58f8fdf4-83a5-4464-9171-8bc5411d93e1"(
                  #"urn:appian:translation-variable:v1:2076c4a6-f99d-4edc-b115-26c836ac0359": joinarray(
                    tointeger(
                      split(
                        cons!CMGT_VRS_CASE_MANAGEMENT_BASE_APPLICATION,
                        "."
                      )
                    ),
                    "."
                  )
                )
              },
              color: local!brandingMap.SecondaryTextColor
            ),
            align: "CENTER",
            marginAbove: "MORE",
            marginBelow: "MORE"
          )
        },
        style: "TRANSPARENT",
        padding: "NONE",
        marginAbove: "NONE",
        showBorder: false
      )
    },
    backgroundColor: local!brandingMap.DefaultBackgroundColor,
    contentsPadding: "STANDARD"
  )
)

