a!localVariables(
  local!allbranches: if(
    ri!context = "MODIFY_WORKFLOW",
    a!flatten(
      ri!tasks[recordType!CMGT_Task.beforeTaskInstanceBranches]
    ),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!tasks,
        expression: rule!CMGT_QR_GetTaskBranch(
          searchTerm: fv!item[recordType!CMGT_Task.taskInstanceUuid],
          searchFields: {
            recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
            recordType!CMGT_TaskBranch.beforeTaskInstanceUuid
          }
        )
      )
    )
  ),
  rule!CMGT_UTIL_DistinctRecords(
    records: {
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!allbranches,
          expression: a!localVariables(
            local!isNewOccurrence: or(
              index(
                index(
                  ri!taskInstanceUuidMapping,
                  wherecontains(
                    touniformstring(
                      fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
                    ),
                    touniformstring(
                      index(
                        ri!taskInstanceUuidMapping,
                        "oldUuid",
                        null()
                      )
                    )
                  ),
                  null()
                ),
                "isNewOccurrenceNeeded",
                null()
              ),
              index(
                index(
                  ri!taskInstanceUuidMapping,
                  wherecontains(
                    touniformstring(
                      fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                    ),
                    touniformstring(
                      index(
                        ri!taskInstanceUuidMapping,
                        "oldUuid",
                        null()
                      )
                    )
                  ),
                  null()
                ),
                "isNewOccurrenceNeeded",
                null()
              )
            ),
            if(
              local!isNewOccurrence,
              {
                #"urn:appian:function:v1:a:update"(
                  data: fv!item,
                  index: recordType!CMGT_TaskBranch.isCurrent,
                  value: false()
                ),
                recordType!CMGT_TaskBranch(
                  recordType!CMGT_TaskBranch.taskBranchId: null,
                  recordType!CMGT_TaskBranch.taskResponseOptionUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid],
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "taskResponseOptionsUuidMapping",
                          {}
                        )
                      ),
                      "taskResponseOptionUUID",
                      null()
                    ),
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "taskResponseOptionsUuidMapping",
                          {}
                        )
                      ),
                      "newUuid",
                      null()
                    ),
                    fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid]
                  ),
                  recordType!CMGT_TaskBranch.beforeTaskInstanceUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid],
                    index(
                      ri!taskInstanceUuidMapping,
                      "oldUuid",
                      {}
                    ),
                    index(
                      ri!taskInstanceUuidMapping,
                      "newUuid",
                      {}
                    ),
                    fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
                  ),
                  recordType!CMGT_TaskBranch.afterTaskInstanceUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid],
                    index(
                      ri!taskInstanceUuidMapping,
                      "oldUuid",
                      {}
                    ),
                    index(
                      ri!taskInstanceUuidMapping,
                      "newUuid",
                      {}
                    ),
                    fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                  ),
                  recordType!CMGT_TaskBranch.isCurrent: if(
                    fv!item[recordType!CMGT_TaskBranch.isCurrent] = false(),
                    false(),
                    true()
                  ),
                  recordType!CMGT_TaskBranch.isRequired: fv!item[recordType!CMGT_TaskBranch.isRequired],
                  recordType!CMGT_TaskBranch.isActive: true,
                  recordType!CMGT_TaskBranch.conditionSetUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.conditionSetUuid],
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "childConditionSetMapping",
                          {}
                        )
                      ),
                      "conditionSetUuid",
                      null()
                    ),
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "childConditionSetMapping",
                          {}
                        )
                      ),
                      "newUuid",
                      null()
                    ),
                    fv!item[recordType!CMGT_TaskBranch.conditionSetUuid]
                  )
                )
              },
              fv!item
            )
          )
        )
      )
    ),
   }
  )
)

_a-0000ecb5-acf8-8000-9cf6-011c48011c48_.sail_code:
a!localVariables(
  local!distinctBeforeTaskUUID: rule!CMGT_UTIL_Distinct(
    ri!caseTypeWorkflowTaskBranch[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid]
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!distinctBeforeTaskUUID,
          expression: a!localVariables(
            local!thisUUID: fv!item,
            local!thisTaskBranches: rule!CMGT_UTIL_Filter(
              records: ri!caseTypeWorkflowTaskBranch,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                  value: local!thisUUID
                ),

              }
            ),
            {
              a!match(
                value: tostring(
                  rule!CMGT_UTIL_Filter(
                    records: ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
                    value: local!thisUUID
                  )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.actionCode]
                ),
                equals: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
                /* Grouping the branches with same Condition Set Uuids */
                then: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!CMGT_UTIL_Distinct(
                    local!thisTaskBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid]
                  ),
                  expression: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                    taskUUID: local!thisUUID,
                    conditionSetUUID: fv!item,
                    afterCaseTypeWorkflowTaskBranches: rule!CMGT_UTIL_Filter(
                      records: local!thisTaskBranches,
                      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid,
                      value: fv!item,

                    ),
                    isRequired: true,
                    isActive: true,
                    isRequirednessValid: true
                  )
                ),
                equals: cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
                /*   Grouping all the branches as the send email dependents are always required and valid      */
                then: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                  taskUUID: local!thisUUID,
                  afterCaseTypeWorkflowTaskBranches: local!thisTaskBranches,
                  isRequired: true,
                  isActive: true,
                  isRequirednessValid: true
                ),
                default: {}
              )
            }
          )
        )
      )
    )
  )
)

