{
  #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: #"urn:appian:translation-string:v1:cf90b3f1-9bae-4f4a-a576-de203e2a320a",
    size: a!defaultValue(ri!textSize, "MEDIUM"),
    color: a!defaultValue(
      ri!textColor,
      "STANDARD"
    )
  ),
  " ",
  a!match(
    value: ri!automationTrigger[recordType!CMGT_CFG_Triggers.triggerId],
    /*case or task field is updated trigger*/
    whenTrue: or(
      tointeger(ri!automationTrigger[recordType!CMGT_CFG_Triggers.triggerId]) = cons!CMGT_REFID_TRIGGER_CASE_FIELD_UPDATED,
      tointeger(ri!automationTrigger[recordType!CMGT_CFG_Triggers.triggerId]) = cons!CMGT_REFID_TRIGGER_TASK_FIELD_IS_UPDATED
    ),
    then: a!localVariables(
      local!fieldPathFromJson: if(
        rule!CMGT_UTIL_IsBlank(
          ri!triggerContext
        ),
        "",
        #"SYSTEM_SYSRULES_fromJson_v1"(
          ri!triggerContext
        )
      ),
      local!uuid: index(
        local!fieldPathFromJson,
        "uuid",
        ""
      ),
      local!fieldDetails: rule!CMGT_UTIL_Filter(
        records: ri!availableFields,
        fieldValuePairs: {
          a!map(
            field: "uuid",
            value: touniformstring(
              local!uuid
            )
          ),
          a!map(
            field: "displayName",
            value: touniformString(
              local!fieldPathFromJson.displayName
            )
          )
        },
        filterType: "FIRST_MATCH"
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: lower(
          if(
            or(
              toboolean(
                search(
                  lower(
                    "case"
                  ),
                  lower(
                    local!fieldDetails.displayName
                  )
                )
              ),
              toboolean(
                search(
                  lower(
                    "task"
                  ),
                  lower(
                    local!fieldDetails.displayName
                  )
                )
              )
            ),
            #"urn:appian:translation-string:v1:8aacea1a-5819-4413-affa-8f166d5ca0df",
            if(
              ri!automationTrigger[recordType!CMGT_CFG_Triggers.triggerId] = cons!CMGT_REFID_TRIGGER_CASE_FIELD_UPDATED,
              #"urn:appian:translation-string:v1:e037247b-ef7e-4e5b-b039-33336bef7cc6",
              #"urn:appian:translation-string:v1:0824d03c-4f64-479b-b888-9d8c9e6e827d"
            )
          ) & " " & local!fieldDetails.displayName & if(
            and(
              rule!CMGT_UTIL_IsNotBlank(
                local!fieldDetails.fieldType
              ),
              typename(
                typeof(
                  local!fieldDetails.fieldType
                )
              ) <> "RecordType",
              or(
                local!fieldDetails.fieldType = "DATETIME",
                local!fieldDetails.fieldType = "DATE"
              )
            ),
            " " & #"urn:appian:translation-string:v1:9406cefa-d79d-4f00-8370-66b1b28739b3",
            null
          ) & " " & #"urn:appian:translation-string:v1:ab752946-2cdb-481b-a768-965023a1dd2e"
        ),
        size: a!defaultValue(ri!textSize, "MEDIUM"),
        style: "STRONG",
        color: a!defaultValue(
          ri!textColor,
          "STANDARD"
        )
      )
    ),
    default: #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: lower(
        ri!automationTrigger[recordType!CMGT_CFG_Triggers.triggerName]
      ),
      size: a!defaultValue(ri!textSize, "MEDIUM"),
