_a-0000ea2f-ac11-8000-9bf4-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(ri!roleTypeIds),
  null,
  a!localVariables(
    local!newUserRoles: rule!CMGT_UTIL_Filter(
      records: ri!roles,
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_Role.roleId,
          value: null()
        ),
        a!map(
          field: recordType!CMGT_Role.roleTypeId,
          value: if(
            ri!roleTypeIds = cons!CMGT_REFID_ROLE_TYPE_WATCHER,
            cons!CMGT_REFID_ROLE_TYPE_WATCHER,
            cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
          )
        )
      }
    ),
    local!currentUserRoles: difference(
      rule!CMGT_UTIL_Filter(
        records: ri!roles,
        fieldValuePairs: {
          a!map(
            field: recordType!CMGT_Role.roleTypeId,
            value: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
          )
        }
      ),
      local!newUserRoles
    ),
    a!match(
      value: ri!roleTypeIds,
      equals: cons!CMGT_REFID_ROLE_TYPE_WATCHER,
      /*Send email to new watchers*/
      then: local!newUserRoles,
      default: a!defaultValue(
        /*Sending Email to new user assignee*/
        value: local!newUserRoles,
        /*
    If there is no current user assignee and if the group assignee is updated, email is sent
    to all group members. If there is a current user assignee, and if the group assignee is updated, the 
    email won't be sent to new group members
    */
        default: if(
          a!isNullOrEmpty(local!currentUserRoles),
          rule!CMGT_UTIL_Filter(
            records: ri!roles,
            fieldValuePairs: {
              a!map(
                field: recordType!CMGT_Role.roleId,
                value: null()
              ),
              a!map(
                field: recordType!CMGT_Role.roleTypeId,
                value: cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
              )
            }
          ),
          null()
        )
      )
    )
  )
)

