_a-0000ed2d-b44d-8000-9d2f-011c48011c48_.sail_code:
if(
  rule!CMGT_UTIL_IsBlank(ri!completedTaskMap),
  {},
  a!localVariables(
    local!caseId: index(
      ri!tasks[recordType!CMGT_Task.caseId],
      1,
      null
    ),
    local!taskBranches: a!flatten(
      ri!tasks[recordType!CMGT_Task.beforeTaskInstanceBranches]
    ),
    local!workflowPaths: rule!CMGT_QE_GetWorkflowPath(caseId: local!caseId, isActive: true),
    rule!CMGT_UTIL_DistinctRecords(
      records: rule!CMGT_UTIL_RejectBlank(
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!completedTaskMap,
          expression: a!localVariables(
            local!currentTask: fv!item.task,
            /*Get workflow paths for the selected response*/
            local!filteredWorkflowPaths: rule!CMGT_UTIL_Filter(
              records: local!workflowPaths,
              fieldValuePairs: {
                a!map(
                  field: "startConditionTaskInstanceUuid",
                  value: local!currentTask[recordType!CMGT_Task.taskInstanceUuid]
                ),
                a!map(
                  field: "taskResponseOptionUuid",
                  value: fv!item.selectedResponseOptionUuid
                ),
                a!map(
                  field: "conditionSetUuid",
                  value: fv!item.satisfiedConditionSetUuid
                )
              }
            ),
            local!previousTasksInTree: rule!CMGT_Tasks_FindPreviousTasksInTree(
              allTasks: ri!tasks,
              taskInstanceUUID: local!currentTask[recordType!CMGT_Task.taskInstanceUuid],
              taskBranches: local!taskBranches,
              fetchOnlyRequiredBeforeBranches: false
            )[recordType!CMGT_Task.taskInstanceUuid],
            /*If the current task is present in the mapping table then there is a loopback.
          In that case return all the tasks. We are taking the distinct as there might be same tasks present on the two or
          more outcomes of the task*/
            local!finalTasksUUIDsToBeReopened: a!flatten(
              rule!CMGT_UTIL_Distinct(
                rule!CMGT_UTIL_RejectBlank(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!filteredWorkflowPaths,
                    expression: a!localVariables(
                      local!currentInitialTaskInstanceUuid: if(
                        rule!CMGT_UTIL_IsBlank(ri!reopenedNextTasks),
                        null,
                        rule!CMGT_UTIL_Filter(
                          records: ri!tasks,
                          field: recordType!CMGT_Task.taskUuid,
                          value: rule!CMGT_UTIL_Filter(
                            records: ri!reopenedNextTasks,
                            field: recordType!CMGT_Task.taskInstanceUuid,
                            value: index(fv!item, "initialTaskInstanceUuid", null)
                          )[recordType!CMGT_Task.taskUuid]
                        )[recordType!CMGT_Task.taskInstanceUuid]
                      ),
                      if(
                        and(
                          contains(
                            index(
                              fv!item.workflowPathMapping,
                              "taskInstanceUuid",
                              null
                            ),
                            local!currentTask[recordType!CMGT_Task.taskInstanceUuid]
                          ),
                          if(
                            rule!CMGT_UTIL_IsNotBlank(ri!reopenedNextTasks),
                            contains(
                              ri!reopenedNextTasks[recordType!CMGT_Task.taskInstanceUuid],
                              index(fv!item, "initialTaskInstanceUuid", null)
                            ),
                            false
                          ),

                        ),
                        rule!CMGT_Task_GetTasksToBeReopenedOnLoopback_Logic(
                          taskBranches: local!taskBranches,
                          previousTasksInTree: local!previousTasksInTree,
                          initialTaskInstanceUuid: local!currentInitialTaskInstanceUuid,
                          tasks: ri!tasks,
                          reopenedTasks:ri!reopenedNextTasks
                        ),
                        null
                      )
                    )
                  )
                )
              )
            ),
            /*if the above task is already completed then change the status to queued as the initial task will already be
          in ready state before coming to this rule*/
            local!finalTasksToReopened: if(
              rule!CMGT_UTIL_IsBlank(local!finalTasksUUIDsToBeReopened),
              null,
              #"urn:appian:function:v1:a:update"(
                rule!CMGT_UTIL_Filter(
                  records: ri!tasks,
                  fieldValuePairs: {
                    a!map(
                      field: recordType!CMGT_Task.taskInstanceUuid,
                      value: local!finalTasksUUIDsToBeReopened
                    ),
                    a!map(
                      field: recordType!CMGT_Task.statusId,
                      value: {
                        cons!CMGT_REFID_STATUS_TASK_COMPLETED,
                        cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED
                      }
                    )
                  }
                ),
                recordType!CMGT_Task.statusId,
                cons!CMGT_REFID_STATUS_TASK_QUEUED
              )
            ),
            local!finalTasksToReopened
          )
        )
      ),
      fields: recordType!CMGT_Task.taskId
    )
  )
)

