a!localVariables(
  /*Get the tasks following this task*/
  local!afterTasks: rule!CMGT_QR_GetTaskBranch(
    beforeTaskId: ri!completedTasks[recordType!CMGT_Task.taskId],
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "OR",
      logicalExpressions: #"SYSTEM_SYSRULES_forEach"(
        items: ri!completedTasks,
        expression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND_ALL",
          ignoreFiltersWithEmptyValues: true,
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
              operator: "=",
              value: fv!item[recordType!CMGT_Task.taskInstanceUuid]
            ),
            /*Ensure that each afterTaskBranch matches the taskReponseUuid of the completedTask if populated*/
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!CMGT_TaskBranch.taskResponseOptionUuid,
              operator: "=",
              value: fv!item[recordType!CMGT_Task.selectedTaskResponseOption.taskResponseOptionUuid]
            )
          }
        )
      )
    ),
    /*Only return next-up tasks that are not Ready and not Inprogress, this is what can be reopened (remember Completed tasks can be reopened)*/
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_TaskBranch.afterTaskInstance.statusId,
        operator: "not in",
        value: rule!CMGT_UTIL_RejectBlank(
          {
            if(
              and(
                ri!completedTasks[recordType!CMGT_Task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                ri!completedTasks[recordType!CMGT_Task.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
                ri!context = "REOPEN"
              ),
              null,
              cons!CMGT_REFID_STATUS_TASK_READY
            ),
            cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS
          }
        )
      )
    },
    fields: {
      recordType!CMGT_TaskBranch.afterTaskInstance,
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113",
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/45ed19eb-2545-4c96-9e19-72127f9d27a3",
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/3280de96-3518-4a9e-b8ae-854a84562f07",
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/5189c3ac-98e2-4a82-8598-2e9561baec9d",
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34",
      #"urn:appian:record-relationship:v1:f00f5631-1637-49bc-8c94-9ea3217339d1/1ef135f7-00f2-4802-aa45-0a8b4299c405/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
      recordType!CMGT_TaskBranch.afterTaskInstance,
      recordType!CMGT_TaskBranch.afterTaskInstance,
      recordType!CMGT_TaskBranch.afterTaskInstance
    }
  )[recordType!CMGT_TaskBranch.afterTaskInstance],
  /*need to get rid of duplicate leads into tasks configured in automations*/
  local!uniqueAfterTasks: union(local!afterTasks, local!afterTasks),
  local!completedAutomationTasks: rule!CMGT_UTIL_Filter(
    records: ri!completedTasks,
    field: recordType!CMGT_Task.automationTaskAction.actionCode,
    value: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
  ),
  /*Determine which tasks to open*/
  local!automationTasksToOpen: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!completedAutomationTasks,
      expression: a!localVariables(
        local!evaluatedConditionSets: a!flatten(
          index(
            index(
              fv!item,
              recordType!CMGT_Task.conditionSet,
              null()
            ),
            recordType!CMGT_ConditionSet.childConditionSet,
            null()
          )
        ),
        local!satisfiedConditionSets: if(
          rule!CMGT_UTIL_IsBlank(local!evaluatedConditionSets),
          null(),
          rule!CMGT_UTIL_RejectBlank(
            a!flatten(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!evaluatedConditionSets,
                expression: a!localVariables(
                  local!conditionsMet: {
                    index(
                      a!flatten(
                        index(
                          fv!item,
                          recordType!CMGT_ConditionSet.condition,
                          null()
                        )
                      ),
                      recordType!CMGT_Condition.isMet,
                      null()
                    )
                  },
                  if(
                    a!match(
                      value: fv!item[recordType!CMGT_ConditionSet.operatorCode],
                      equals: cons!CMGT_TXT_SET_OPERATOR_OR,
                      then: or(local!conditionsMet),
                      equals: cons!CMGT_TXT_SET_OPERATOR_AND,
                      then: and(local!conditionsMet),
                      default: false()
                    ),
                    fv!item,
                  null()
                ))
              )
            )
          )
        ),
        local!afterTaskBranches: rule!CMGT_UTIL_Filter(
          records: rule!CMGT_UTIL_Filter(
            records: a!flatten(
              index(
                local!uniqueAfterTasks,
                recordType!CMGT_Task.beforeTaskInstanceBranches,
                null()
              )
            ),
            field: recordType!CMGT_TaskBranch.isCurrent,
            value: true()
          ),
          field: recordType!CMGT_TaskBranch.beforeTaskInstance.taskInstanceUuid,
          value: fv!item[recordType!CMGT_Task.taskInstanceUuid]
        ),
        local!afterTaskInstanceUUIDs: if(
          rule!CMGT_UTIL_IsBlank(value: local!satisfiedConditionSets),
          /*  Logic to return default automation after tasks to open             */
          index(
            rule!CMGT_UTIL_Filter(
              records: local!afterTaskBranches,
              field: recordType!CMGT_TaskBranch.conditionSetUuid,
              value: null()
            ),
            recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
            null()
          ),
          /*    Logic to return the automation after tasks to open            */
          index(
            rule!CMGT_UTIL_Filter(
              records: local!afterTaskBranches,
              field: recordType!CMGT_TaskBranch.conditionSetUuid,
              value: rule!CMGT_UTIL_Distinct(
                value: local!satisfiedConditionSets[recordType!CMGT_ConditionSet.conditionSetUuid]
              )
            ),
            recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
            null()
          )
        ),
        local!finalAutomationTasksToOpen: rule!CMGT_UTIL_RejectBlank(
          array: #"SYSTEM_SYSRULES_forEach"(
            items: rule!CMGT_UTIL_Filter(
              records: local!uniqueAfterTasks,
              field: recordType!CMGT_Task.taskInstanceUuid,
              value: local!afterTaskInstanceUUIDs
            ),
            /*If all the  required branches satisfied, it means this task can be opened*/
            expression: a!localVariables(
              local!currentTask: fv!item,
              local!requiredBeforeBranches: rule!CMGT_UTIL_Filter(
                records: rule!CMGT_UTIL_Filter(
                  records: a!flatten(
                    index(
                      local!currentTask,
                      recordType!CMGT_Task.beforeTaskInstanceBranches,
                      null()
                    )
                  ),
                  field: recordType!CMGT_TaskBranch.isCurrent,
                  value: true
                ),
                field: recordType!CMGT_TaskBranch.isRequired,
                value: true
              ),
              if(
                and(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!requiredBeforeBranches,
                    expression: and(
                      fv!item[recordType!CMGT_TaskBranch.beforeTaskInstance.isCompleted] = true(),
                      if(
                        fv!item[recordType!CMGT_TaskBranch.beforeTaskInstance.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                        fv!item[recordType!CMGT_TaskBranch.beforeTaskInstance.selectedTaskResponseOption.taskResponseOptionUuid] = fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid],
                        true
                      )
                    )
                  )
                ),
                local!currentTask,
                {}
              )
            )
          )
        ),
        { local!finalAutomationTasksToOpen }
      )
    )
  ),
  local!otherTasksToOpen: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!uniqueAfterTasks,
      expression: a!localVariables(
        local!thisTask: fv!item,
        local!taskBranches: rule!CMGT_UTIL_Filter(
          records: local!thisTask[recordType!CMGT_Task.beforeTaskInstanceBranches],
          field: recordType!CMGT_TaskBranch.isCurrent,
          value: true()
        ),
        local!workflowRoutingAutomationTaskBranches: rule!CMGT_UTIL_Filter(
          records: rule!CMGT_UTIL_Filter(
            records: local!taskBranches,
            field: recordType!CMGT_TaskBranch.beforeTaskInstance.taskBehaviorTypeId,
            value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
          ),
          field: recordType!CMGT_TaskBranch.beforeTaskInstance.automationTaskAction.actionCode,
          value: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
        ),
        /*Get the required branches leading into this task -- that's all we have to check at this stage*/
        local!requiredBeforeBranches: difference(
          rule!CMGT_UTIL_Filter(
            records: rule!CMGT_UTIL_Filter(
              records: local!thisTask[recordType!CMGT_Task.beforeTaskInstanceBranches],
              field: recordType!CMGT_TaskBranch.isCurrent,
              value: true
            ),
            field: recordType!CMGT_TaskBranch.isRequired,
            value: true
          ),
          local!workflowRoutingAutomationTaskBranches
        ),
        /*Determine which branches have yet to be satisfied*/
        /*At this stage we only have to confirm that there aren't required branches that arne't satisfied*/
        local!unsatisfiedRequiredBeforeBranches: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!requiredBeforeBranches,
            expression: if(
              /*If the task is not complete, or it's complete but the response option is incorrect, consider it unsatsified*/
              or(
                not(
                  fv!item[recordType!CMGT_TaskBranch.beforeTaskInstance.isCompleted]
                ),
                fv!item[recordType!CMGT_TaskBranch.beforeTaskInstance.selectedTaskResponseOption.taskResponseOptionUuid] <> fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid]
              ),
              fv!item,
              {}
            )
          )
        ),
        /*If there are no unsatisfied required branches, it means this task can be opened*/
        if(
          and(
            a!isNullOrEmpty(
              local!unsatisfiedRequiredBeforeBranches
            ),
            or(
              a!isNullOrEmpty(
                value: local!workflowRoutingAutomationTaskBranches
              ),
              and(
                ri!completedTasks[recordType!CMGT_Task.taskBehaviorTypeId] <> cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                ri!completedTasks[recordType!CMGT_Task.automationTaskAction.actionCode] <> cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
              )
            )
          ),
          fv!item,
          {}
        )
      )
    )
  ),
  local!tasksToOpen: a!flatten(
    {
      local!automationTasksToOpen,
      local!otherTasksToOpen
    }
  ),
  if(
    a!isNotNullOrEmpty(local!tasksToOpen),
    /*All next tasks are opened irrespective of milestone    */
    {
      tasks: #"urn:appian:function:v1:a:update"(
        local!tasksToOpen,
        recordType!CMGT_Task.statusId,
        cons!CMGT_REFID_STATUS_TASK_READY
      ),
      originalTasks: local!tasksToOpen
    },
    { tasks: {}, originalTasks: {} }
  )
)

