if(
  rule!CMGT_UTIL_IsBlank(value: ri!completedTaskMap),
  null(),
  a!localVariables(
    local!caseId: index(
      ri!tasks[recordType!CMGT_Task.caseId],
      1,
      null
    ),
    local!workflowPaths: rule!CMGT_QE_GetWorkflowPath(caseId: local!caseId, isActive: true),
    local!completedTaskMap: #"SYSTEM_SYSRULES_forEach"(
      items: ri!completedTaskMap,
      expression: a!localVariables(
        local!thisTask: index(fv!item, "task", null()),
        local!thisWorkflowPaths: rule!CMGT_UTIL_Filter(
          records: local!workflowPaths,
          field: "startConditionTaskInstanceUuid",
          value: local!thisTask[recordType!CMGT_Task.taskInstanceUuid]
        ),
        /*Filter the traversed workflow path from all the workflow paths queried */
        local!traversedWorkflowPath: rule!CMGT_UTIL_Filter(
          records: local!thisWorkflowPaths,
          fieldValuePairs: {
            a!map(
              field: "taskResponseOptionUuid",
              value: fv!item.selectedResponseOptionUuid
            ),
            a!map(
              field: "conditionSetUuid",
              value:fv!item.satisfiedConditionSetUuid
            )
          }
        ),
        /*Filter the Untraversed workflow paths from all the workflow paths queried */
        local!untraversedWorkflowPath: rule!CMGT_UTIL_Filter(
          records: local!thisWorkflowPaths,
          fieldValuePairs: {
            a!map(
              field: "taskResponseOptionUuid",
              value: fv!item.selectedResponseOptionUuid
            ),
            a!map(
              field: "conditionSetUuid",
              value: fv!item.satisfiedConditionSetUuid
            )
          },
          filterType: "REJECT"
        ),
        /*Get all the untraversed path mappings*/
        local!untraversedWorkflowPathMapping: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!untraversedWorkflowPath,
            expression: index(fv!item, "workflowPathMapping", null)
          )
        ),
        local!traversedWorkflowPathMapping: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!traversedWorkflowPath,
            expression: index(fv!item, "workflowPathMapping", null)
          )
        ),
        local!tasksInUntraversedPath: rule!CMGT_UTIL_Filter(
          records: ri!tasks,
          field: recordType!CMGT_Task.taskInstanceUuid,
          value: index(
            local!untraversedWorkflowPathMapping,
            "taskInstanceUuid",
            null
          )
        ),
        /*Conditions to check for the task inorder to mark the unchosen path as Not Needed: 
-- If the task is present in traversed path - Ignore.
-- If the task has branches other than that from the untraversed path, Ignore
-- If the task has branches from TasksToOpen (Ready tasks), Ignore  */
        /*Filter Queued Tasks*/
        local!queuedTasks: rule!CMGT_UTIL_Filter(
          records: local!tasksInUntraversedPath,
          field: recordType!CMGT_Task.statusId,
          value: cons!CMGT_REFID_STATUS_TASK_QUEUED
        ),
        /*Condition 1: Filter queued tasks which are not in Chosen Path */
        local!tasksNotOnChosenPath: rule!CMGT_UTIL_Filter(
          records: local!queuedTasks,
          field: recordType!CMGT_Task.taskInstanceUuid,
          value: index(
            local!traversedWorkflowPathMapping,
            "taskInstanceUuid",
            null
          ),
          filterType: "REJECT"
        ),
        a!map(
          taskInstanceUUID: local!thisTask[recordType!CMGT_Task.taskInstanceUuid],
          tasksInUntraversedPath: local!tasksNotOnChosenPath
        )
      )
    ),
    /*Reducer function to check the below conditions*/
    /*Condition 2 & 3: Check whether the task has any other before tasks
        if YES, Check the status of the before task. If the status is 'Ready', 'In Progress' or 'Queued' - Ignore*/
    local!finalMap: reduce(
      rule!CMGT_Task_GetNotNeededTasks_Reducer(
        finalMap: _,
        completedTaskMap: _,
        tasksToOpenUuids: ri!tasksToOpenUuids,
        allCompletedTaskMap: local!completedTaskMap
      ),
      a!map(
        notNeededtaskUUIDs: null,
        dependentTaskMap: null,
        taskUuidsRemovedFromNotNeeded: null
      ),
      local!completedTaskMap
    ),
    /*Filter the tasks to be marked as 'Not Needed'*/
    local!notNeededTasks: rule!CMGT_UTIL_Filter(
      records: ri!tasks,
      field: recordType!CMGT_Task.taskInstanceUuid,
      value: local!finalMap.notNeededtaskUUIDs
    ),
    if(
      rule!CMGT_UTIL_IsBlank(value: local!notNeededTasks),
      {},
      #"urn:appian:function:v1:a:update"(
        data: local!notNeededTasks,
        index: recordType!CMGT_Task.statusId,
        value: cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED
      )
    )
  )
)

000be998-4eda-8000-fc2f-7f0000014e7a.sail_code:
// Input: ?
=rule!CMGT_Email_EmailSettingMap_Assignees(
  roles: pv!tasksToOpen[recordType!CMGT_Task.roles],
  isTaskOpened: true
)

// Input: ?
rule!CMGT_Task_WriteRecordsProperties(
  tasks: pv!tasksToOpen,
  isInput: true
)

// Input: ?
=recordType!CMGT_Task

// Output: ?
rule!CMGT_Task_WriteRecordsProperties(
  tasks: pv!tasksToOpen,
  recordsUpdated: ac!RecordsUpdated,
  isInput: false
)

// Input: ?
remove(
  rule!CMGT_UTIL_RejectBlank(array: { pv!updatedTaskBranches }),
  {
    recordType!CMGT_TaskBranch.beforeTaskInstance,
    recordType!CMGT_TaskBranch.afterTaskInstance
  }
)

// Input: ?
=recordType!CMGT_TaskBranch

// Output: ?
rule!CMGT_Task_WriteRecordsProperties(
  tasks: pv!tasksToOpen,
  recordsUpdated: ac!RecordsUpdated,
  isInput: false
)

// Input: ?
=rule!CMGT_Task_ConstructTaskInstanceUuidMapping( activeTasks: rule!CMGT_Task_ConstructRelationshipsForActiveTasks(tasks: pv!tasksToOpen))

// Output: ?
rule!CMGT_Task_ConstructTaskAndTaskOccurrence(
  tasks: pv!tasksToOpen,
  taskInstanceUuidMapping: ac!taskInstanceUuidMapping
)

// Output: ?
rule!CMGT_Task_UpdateTaskBranchesForNewTaskInstance(
  tasks: pv!tasksToOpen,
  taskInstanceUuidMapping: ac!taskInstanceUuidMapping
)

// Input: ?
= rule!CMGT_QR_GetCase(
  caseId: rule!CMGT_UTIL_Distinct(
    value: pv!tasksToOpen[recordType!CMGT_Task.caseId]
  ),
  fields: {
    recordType!CMGT_Case.caseId,
    recordType!CMGT_Case.folderId,
    recordType!CMGT_Case.friendlyId
  }
)

// Input: ?
=pv!updatedEmailTemplateAttachments

// Input: ?
index(
  rule!CMGT_UTIL_RejectBlank(
    array: {
      union(
        tointeger(
          index(
            pv!tasksToOpen,
            recordType!CMGT_Task.caseId,
            null
          )
        ),
        tointeger(
          index(
            pv!createdTasks,
            recordType!CMGT_Task.caseId,
            null
          )
        ),
        tointeger(
          index(
            pv!completedTasks,
            recordType!CMGT_Task.caseId,
            null
          )
        )
      )
    }
  ),
  1,
  null()
)

// Input: ?
"UPDATE"

// Output: ?
rule!CMGT_Task_SetMetaData(
  tasks: pv!tasksToOpen,
  initiator: cons!CMGT_TXT_PROCESS_AUTOMATION,
  eventType: "OPEN_NEXT_TASK"
)

// Output: ?
rule!CMGT_SUB_OpenNextTasks_Logic_FromCreatedTasks(createdTasks: pv!createdTasks)

// Output: ?
pv!createdTasks

// Input: ?
= recordType!CMGT_Case(
  recordType!CMGT_Case.caseId: a!flatten(
    index(
      a!defaultValue(pv!completedTasks, pv!createdTasks),
      recordType!CMGT_Task.caseId,
      null
    )
  )[1]
)

// Input: ?
= rule!CMGT_Automation_ReturnTaskFieldDifferences(
  originalTask: rule!CMGT_UTIL_Filter(
    records: pv!originalTasks,
    field: recordType!CMGT_Task.taskInstanceUuid,
    value: pv!tasksToOpen[recordType!CMGT_Task.taskInstanceUuid][tp!instanceindex]
  ),
  task: pv!tasksToOpen[tp!instanceindex]
)

// Input: ?
=cons!CMGT_REFID_TRIGGER_TASK_FIELD_IS_UPDATED

// Input: ?
=pv!tasksToOpen[tp!instanceindex]

// Output: ?
a!flatten(
  #"SYSTEM_SYSRULES_forEach"(
    items: rule!CMGT_UTIL_Filter(
      records: pv!tasksToOpen,
      field: recordType!CMGT_Task.isCurrent,
      value: true
    ),
    expression: if(
      rule!CMGT_UTIL_IsBlank(
        fv!item[recordType!CMGT_Task.sendEmailTask]
      ),
      {},
      #"urn:appian:function:v1:a:update"(
        fv!item[recordType!CMGT_Task.sendEmailTask],
        recordType!CMGT_EmailTemplateAttachments.sendEmailTaskId,
        fv!item[recordType!CMGT_Task.sendEmailTask.sendEmailTaskId]
      )
    )
  )
)

// Input: ?
= rule!CMGT_SUB_OpenNextTasks_Logic_FromCompletedTasks(completedTasks: pv!completedTasks, context: pv!context)

// Output: ?
/*We are taking distinct as there is a possibility of returning same tasks*/
rule!CMGT_UTIL_DistinctRecords(
  records: ac!nextTasksEvaluation.tasks,
  fields: recordType!CMGT_Task.taskId
)

// Output: ?
rule!CMGT_UTIL_DistinctRecords(
  records: ac!nextTasksEvaluation.originalTasks,
  fields: recordType!CMGT_Task.taskId
)

// Output: ?
rule!CMGT_Automation_IncreaseLoopExecutionCount(
  automationTaskUuids: rule!CMGT_UTIL_Distinct(
    rule!CMGT_UTIL_Filter(
      records: pv!tasksToOpen,
      field: recordType!CMGT_Task.taskBehaviorTypeId,
      value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
    )[recordType!CMGT_Task.taskUuid]
  ),
  automationEvaluationLoopExecutions: pv!automationEvaluationLoopExecutions
)

// Input: ?
=pv!automationEvaluationLoopExecutions

// Input: ?
=if(
  rule!CMGT_UTIL_IsNotBlank(
    pv!tasksToOpen[recordType!CMGT_Task.taskId]
  ),
  rule!CMGT_QR_GetTask(
    taskId: pv!tasksToOpen[recordType!CMGT_Task.taskId],
    includeRoles: true(),
    includeBranches: true(),
    includeMilestone: true()
  ),
  null()
)

// Input: ?
={ {
  rule!CMGT_Task_SetMetaData(
    tasks: pv!originalTasks,
    initiator: cons!CMGT_TXT_PROCESS_AUTOMATION,
    eventType: "INFINITE_AUTOMATION_EXIT"
  )
}}

// Input: ?
={recordType!CMGT_Task}

// Input: ?
=if(
  rule!CMGT_UTIL_IsNotBlank(pv!completedTasks),
  rule!CMGT_QR_GetTask(
    taskId: pv!completedTasks[recordType!CMGT_Task.taskId],
    includeBranches: true()
  ),
  null
)

// Input: ?
=if(
  rule!CMGT_UTIL_IsNotBlank(pv!completedTasks),
  index(
    pv!completedTasks[recordType!CMGT_Task.caseId],
    1,
    null
  ),
  null
)

// Input: ?
=/*Since the workflow mapping is not updated we are passing old values*/
rule!CMGT_UTIL_Filter(
  records: pv!tasksToOpen,
  field: recordType!CMGT_Task.isCurrent,
  value: false
)

// Input: ?
=automationEvaluationLoopExecutions

