a!localVariables(
  local!originalTasks: a!defaultValue(
    ri!originalTasks,
    if(
      rule!CMGT_UTIL_IsBlank(ri!activeTasks),
      null,
      rule!CMGT_QR_GetTask(
        taskId: ri!activeTasks[recordType!CMGT_Task.taskId],
        includeInactive: true,
        executeWhen: rule!CMGT_UTIL_IsNotBlank(
          a!flatten(
            ri!activeTasks[recordType!CMGT_Task.taskId]
          )
        ),
        taskIntentionCodes: {
          cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER,
          cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_WORKFLOW
        },
        includeMilestone: true(),
        fields: {
          recordType!CMGT_Task.beforeTaskInstanceBranches,
          recordType!CMGT_Task.conditionSet,
          recordType!CMGT_Task.taskResponseOptions,
          recordType!CMGT_Task.conditionSet,
          recordType!CMGT_Task.conditionSet,
          #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
          recordType!CMGT_Task.automationTaskAction
        }
      )
    )
  ),
  local!originalAutomationWorkflowTasks: if(
    rule!CMGT_UTIL_IsBlank(ri!activeTasks),
    null,
    rule!CMGT_QR_GetTask(
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
          operator: "in",
          value: ri!activeTasks[recordType!CMGT_Task.taskInstanceUuid]
        )
      },
      fields: {
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.conditionSet,
        #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
        recordType!CMGT_Task.automationTaskAction
      },
      taskIntentionCodes: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER,
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        a!flatten(
          ri!activeTasks[recordType!CMGT_Task.taskId]
        )
      )
    )
  ),
  local!taskInstanceUuidMapping: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!activeTasks,
      expression: a!localVariables(
        /*The current value of this record in the database*/
        local!orgItem: rule!CMGT_UTIL_Filter(
          records: local!originalTasks,
          field: recordType!CMGT_Task.taskId,
          value: fv!item[recordType!CMGT_Task.taskId]
        ),
        local!taskInstanceUuid: rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
        local!isNewOccurrenceNeeded: /* If a config task gets pass in, then we always need to create a new occurence  */
        or(
          local!orgItem[recordType!CMGT_Task.taskIntentionCode] = cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER,
          rule!CMGT_CheckIfTaskOccurrenceNeeded(
            originalStatus: local!orgItem[recordType!CMGT_Task.statusId],
            newStatus: fv!item[recordType!CMGT_Task.statusId]
          )
        ),
        a!flatten(
          a!map(
            isNewOccurrenceNeeded: local!isNewOccurrenceNeeded,
            taskId: fv!item[recordType!CMGT_Task.taskId],
            oldUuid: fv!item[recordType!CMGT_Task.taskInstanceUuid],
            newUuid: if(
              local!isNewOccurrenceNeeded,
              local!taskInstanceUuid,
              fv!item[recordType!CMGT_Task.taskInstanceUuid]
            ),
            taskResponseOptionsUuidMapping: #"SYSTEM_SYSRULES_forEach"(
              items: fv!item[recordType!CMGT_Task.taskResponseOptions],
              expression: a!map(
                taskResponseOptionUUID: fv!item[recordType!CMGT_TaskResponseOption.taskResponseOptionUuid],
                newUuid: if(
                  local!isNewOccurrenceNeeded,
                  rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
                  fv!item[recordType!CMGT_TaskResponseOption.taskResponseOptionUuid]
                )
              )
            ),
            parentConditionSetMapping: #"SYSTEM_SYSRULES_forEach"(
              items: fv!item[recordType!CMGT_Task.conditionSet],
              expression: a!localVariables(
                local!item: fv!item,
                a!map(
                  conditionSetUuid: local!item[recordType!CMGT_ConditionSet.conditionSetUuid],
                  newUuid: if(
                    local!isNewOccurrenceNeeded,
                    rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
                    local!item[recordType!CMGT_ConditionSet.conditionSetUuid]
                  )
                )
              )
            ),
            childConditionSetMapping: #"SYSTEM_SYSRULES_forEach"(
              items: fv!item[recordType!CMGT_Task.conditionSet],
              expression: a!localVariables(
                local!item: fv!item,
                a!map(
                  conditionSetUuid: local!item[recordType!CMGT_ConditionSet.conditionSetUuid],
                  newUuid: if(
                    local!isNewOccurrenceNeeded,
                    rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
                    local!item[recordType!CMGT_ConditionSet.conditionSetUuid]
                  )
                )
              )
            ),
            taskActionUuidMapping:
              if(
              rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.automationTaskAction]
              ),
              #"SYSTEM_SYSRULES_forEach"(
                items: fv!item[recordType!CMGT_Task.automationTaskAction],
                expression: a!map(
                  taskActionUuid: fv!item[recordType!CMGT_TaskActions.taskActionUuid],
                  newUuid:rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
                ),
              ),
              null
            ),
            /*Generates relevant automation rules for the new instance of the task*/
            automationRuleTasksSetMapping: rule!CMGT_Task_ConstructTaskInstanceUuidMapping(
              activeTasks: rule!CMGT_UTIL_Filter(
                records: local!originalAutomationWorkflowTasks,
                field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
                value: fv!item[recordType!CMGT_Task.taskInstanceUuid]
              )
            )
          )
        )
      )
    )
  ),
  local!taskInstanceUuidMapping
)

