  },
  fields: {
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskIntentionCode/*needed so the automation rules do not appear in the workflow grid/visualization on the case*/,
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction,
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction,
    #"urn:appian:record-relationship:v1:d6ebee27-6e86-47e3-a9b8-71fdd0348854/b80565b5-ff65-4f64-8007-f2382adffd3f/de7a72ca-c484-48d9-974a-f834e5d302bd/69201ed0-4d96-49e9-805a-4bcf4d59ab9d",
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.conditionSet,
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTrigger,
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.triggerContext,
    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeUuid/*to show the assignment rule selected*/

  },
  additionalLogicalExpression: {
    #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.isActive,
          operator: "=",
          value: true
        ),
        /*Should not pull in any task specific automation rules        */
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationParentCaseTypeWorkflowTaskUuid,
          operator:"is null"
        )
      },
      logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        logicalExpressions: {
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeUuid,
                operator: "is null"
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeUuid,
                operator: "=",
                value: ri!case[recordType!CMGT_Case.caseTypeUuid],
                applyWhen: a!isNotNullOrEmpty(
                  ri!case[recordType!CMGT_Case.caseTypeUuid]
                )
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeUuid,
                operator: "=",
                value: ri!case[recordType!CMGT_Case.caseType.parentUuid],
                applyWhen: a!isNotNullOrEmpty(
                  ri!case[recordType!CMGT_Case.caseType.parentUuid]
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "OR",
            logicalExpressions: {
              /*filters on rules where the trigger does not need any context*/
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTrigger.triggerId,
                    operator: "in",
                    value: ri!triggerIds,
                    applyWhen: rule!CMGT_UTIL_IsNotBlank(
                      wherecontains(
                        ri!triggerIds,
                        {
                          cons!CMGT_REFID_TRIGGER_CASE_CREATED,
                          cons!CMGT_REFID_TRIGGER_CASE_UPDATED
                        }
                      )
                    )
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.triggerContext,
                    operator: "is null",
                    applyWhen: rule!CMGT_UTIL_IsNotBlank(
                      wherecontains(
                        ri!triggerIds,
                        {
                          cons!CMGT_REFID_TRIGGER_CASE_CREATED,
                          cons!CMGT_REFID_TRIGGER_CASE_UPDATED
                        }
                      )
                    )
                  )
                }
              ),
              /*filters on rules where the context is needed*/
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTrigger.triggerId,
                    operator: "in",
                    value: ri!triggerIds,
                    applyWhen: and(
                      rule!CMGT_UTIL_IsNotBlank(ri!triggerContext),

                    )
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.triggerContext,
                    operator: "in",
                    value: ri!triggerContext,
                    applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!triggerContext)
                  )
                }
              )
            },
            filters: {}
          )
        }
      )
    )
  },
  sort: {
    #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeUuid,
      ascending: true
    ),
    #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sortOrder,
      ascending: true
    )
  }
)

