a!match(
  value: ri!context,
  whenTrue: rule!CMGT_UTIL_IsBlank(ri!comments),
  then: {},
  equals: "CASE",
  then: a!flatten(
    {
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!comments,
        expression: rule!CMGT_CaseEventHistory_Constructor(
          caseId: fv!item[recordType!CMGT_Comment.caseId],
          initiator: ri!initiator,
          eventTypeId:           
          /*comment event history*/
          if(
            fv!item[recordType!CMGT_Comment.isActive] = false,
            cons!CMGT_REFID_EVENT_TYPE_CASE_DELETE_COMMENT,
            if(
              a!isNotNullOrEmpty(
                fv!item[recordType!CMGT_Comment.commentId]
              ),
              cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_COMMENT,
              if(
                a!isNotNullOrEmpty(
                  fv!item[recordType!CMGT_Comment.parentCommentId]
                ),
                cons!CMGT_REFID_EVENT_TYPE_CASE_REPLY_TO_COMMENT,
                cons!CMGT_REFID_EVENT_TYPE_CASE_ADD_COMMENT
              )
            )
          ),
          details: a!localVariables(
            local!detailsMap: a!map(
              commentText: fv!item[recordType!CMGT_Comment.commentText],
              taggedUsers: if(
                rule!CMGT_UTIL_IsBlank(
                  ri!comments[recordType!CMGT_Comment.userTags]
                ),
                {},
                rule!CMGT_UTIL_RejectBlank(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: a!flatten(
