a!localVariables(
  local!dateHex: dec2hex(text(today(), "mmddyy")),
  local!asciiCodes: append(
    /* ASCII codes for A-Z (65 - 90) */
    enumerate(26) + 65,
    /* ASCII codes for 0-9 (48 - 57) */
    enumerate(10) + 48
  ),
  /* Generating 6 random alphanumeric characters */
  local!randomAlphaNumericChars: concat(
    char(
      index(
        local!asciiCodes,
        1 + round(rand(6) * (count(local!asciiCodes) - 1), 0),
        null
      )
    )
  ),
  joinarray(
    {
      a!defaultValue(
        value: ri!case[recordType!CMGT_Case.caseType.caseTypeMetadata.prefix],
        default: rule!CMGT_setPrefixDefault(
          caseType: ri!case[recordType!CMGT_Case.caseType]
        )
      ),
      "-",
      local!randomAlphaNumericChars,
      local!dateHex
    }
  )
)

