if(
  a!isNullOrEmpty(ri!startDate),
  {},
  a!localVariables(
    local!daysInAWeek: 7,
    local!month: month(ri!startDate),
    local!year: year(ri!startDate),
    /** Create initial array of dates for current month **/
    local!numOfDaysInMonth: daysinmonth(local!month, local!year),
    local!arrayOfDates: #"SYSTEM_SYSRULES_forEach"(
      items: enumerate(local!numOfDaysInMonth) + 1,
      expression: date(local!year, local!month, fv!item)
    ),
    /** Prepend array with last month's dates **/
    local!firstDayOfMonth: date(local!year, local!month, 1),
    local!monthStartDayOfWeek: weekday(local!firstDayOfMonth),
    local!numOfDaysToPrepend: local!monthStartDayOfWeek - 1,
    local!lastDayOfMonth: date(
      local!year,
      local!month,
      local!numOfDaysInMonth
    ),
    local!previousMonth: month(
      local!lastDayOfMonth - local!numOfDaysInMonth
    ),
    local!previousYear: year(
      local!lastDayOfMonth - local!numOfDaysInMonth
    ),
    local!numOfDaysInPrevMonth: daysinmonth(local!previousMonth, local!previousYear),
    local!datesToPrepend: #"SYSTEM_SYSRULES_forEach"(
      items: if(
        local!numOfDaysToPrepend = 0,
        {},
        index(
          reverse(
            enumerate(local!numOfDaysInPrevMonth) + 1
          ),
          { enumerate(local!numOfDaysToPrepend) + 1 },
          {}
        )
      ),
      expression: date(
        local!previousYear,
        local!previousMonth,
        fv!item
      )
    ),
    local!arrayWithPrependedDates: reduce(
      fn!insert,
      local!arrayOfDates,
      local!datesToPrepend,
      1
    ),
    /** Append array with next month's dates **/
    local!monthEndDayOfWeek: weekday(
      date(
        local!year,
        local!month,
        local!numOfDaysInMonth
      )
    ),
    local!numOfDaysToAppend: local!daysInAWeek - local!monthEndDayOfWeek,
    local!nextMonth: month(
      local!firstDayOfMonth + local!numOfDaysInMonth
    ),
    local!nextYear: year(
      local!firstDayOfMonth + local!numOfDaysInMonth
    ),
    local!datesToAppend: #"SYSTEM_SYSRULES_forEach"(
      items: if(
        local!numOfDaysToAppend = 0,
        {},
        #"SYSTEM_SYSRULES_forEach"(
          items: enumerate(local!numOfDaysToAppend) + 1,
          expression: tostring(fv!item)
        )
      ),
      expression: date(local!nextYear, local!nextMonth, fv!item)
    ),
    local!arrayWithAppendedDates: reduce(
      fn!append,
      local!arrayWithPrependedDates,
      local!datesToAppend
    ),
    local!arrayWithAppendedDates
  )
)

