  local!dependentEdges: a!refreshVariable(
    value:rule!CMGT_UTIL_Filter(
      records: ri!allEdges,
      field: "from",
      value: ri!currentNode.id
    ),
    refreshAlways: true
  ),
  local!dependentNodes: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: ri!orderedNodes,
      field: "id",
      value: local!dependentEdges.to
    ),
    refreshAlways: true
  ),
  /*setting this as a percent 1-100*/
  /*if there are no children the score is 50*/
  local!averageChildHorizontalOrder: a!refreshVariable(
