  searchTerm: ri!searchTerm,
  searchFields: ri!searchFields
)

_a-0000ed40-282d-8000-9d33-011c48011c48_.sail_code:
a!localVariables(
  /*Filter after task instance uuid of the current task*/
  local!beforeTaskInstanceUuids: rule!CMGT_UTIL_Distinct(
    index(
      rule!CMGT_UTIL_Filter(
        records: ri!dependentMap,
        fieldValuePairs: {
          a!map(
            field: "dependentTask",
            value: ri!currentTaskInstanceUuid
          ),

        }
      ),
      "currentTask",
      null
    )
  ),
  /*Append the current task instance UUID with the final path mapping tasks*/
  local!finalTaskUuids: rule!CMGT_UTIL_Distinct(
    append(
      ri!finalTaskUuids,
      ri!currentTaskInstanceUuid
    )
  ),
  if(
    /*Recursion END Condition*/
    /*Check if there are any after task for current task or the current task
      leads to loopback (Task present in final output of workflow path mapping)*/
    or(
      rule!CMGT_UTIL_IsBlank(local!beforeTaskInstanceUuids),
      contains(
        ri!finalTaskUuids,
        ri!currentTaskInstanceUuid
      )
    ),
    /*Return the result if recursion end condition is met*/
    local!finalTaskUuids,
    /*Call the recursion logic again with the filtered afterTaskUUIDs*/
    reduce(
      rule!CMGT_Task_GetDependentTask_Reducer(
        finalTaskUuids: _,
        currentTaskInstanceUuid: _,
        dependentMap: ri!dependentMap
      ),
      local!finalTaskUuids,
      local!beforeTaskInstanceUuids
    )
  )
)

