      ri!fieldPathJson
    ),
    "",
    #"SYSTEM_SYSRULES_fromJson_v1"(
      ri!fieldPathJson
    ).
  ),
  local!fieldUuid: index(local!fieldPathFromJson, "uuid", ""),
  local!fieldDetails: rule!CMGT_UTIL_Filter(
    records: ri!fieldDetails,
    fieldValuePairs: {
      a!map(
        field: "uuid",
        value: touniformstring(local!fieldUuid)
      ),
      a!map(
        field: "displayName",
        value: touniformString(index(local!fieldPathFromJson, "displayName",null))
      ),
      if(
        rule!CMGT_UTIL_IsNotBlank(index(local!fieldPathFromJson, "customFieldCode", null)),
        a!map(
          field: "customFieldCode",
          value: touniformString(index(local!fieldPathFromJson, "customFieldCode", null))
        ),
        {}
      )
    },
    filterType: "FIRST_MATCH"
  ),
  local!jsonValue: if(
    /*if boolean operators, value can be null. hence check for nulls */
    a!isNullOrEmpty(
      ri!valueJson
    ),
    "",
    #"SYSTEM_SYSRULES_fromJson_v1"(
      ri!valueJson
    ).value
  ),
  local!value: rule!CMGT_Automation_returnConditionValueForDisplay(
    fieldDetails: local!fieldDetails,
    jsonValue: local!jsonValue,
    fieldPathFromJson: local!fieldPathFromJson,
    operator: ri!operator
  ),
  {
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: local!fieldDetails.label & " ",
      style: "STRONG",
      size: a!defaultValue(ri!textSize, "MEDIUM"),
      color: a!defaultValue(ri!textColor, "STANDARD")
    ),
    /*formats the "Equal to" and "Not equal to" operators to include "is" at the front, otherwise returns the stores operator*/
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: a!match(
        value: ri!operator,
        whenTrue: or(
          fv!value = #"urn:appian:translation-string:v1:a4915261-2829-446b-87c7-760583581794",
          fv!value = #"urn:appian:translation-string:v1:d5afe4a4-04fc-43db-b06b-48fc966579ba"
        ),
        then: #"urn:appian:translation-string:v1:bbb9aa09-db97-467f-a73e-f1dba4eee928" & " " & lower(
          ri!operator
        ),
        default: lower(
          ri!operator
        )
      ),
      size: a!defaultValue(ri!textSize, "MEDIUM"),
      color: a!defaultValue(ri!textColor, "STANDARD")
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: " ",
      showWhen: or(
        rule!CMGT_UTIL_IsNotBlank(local!value)
      ),
      size: a!defaultValue(ri!textSize, "MEDIUM"),
      color: a!defaultValue(ri!textColor, "STANDARD")
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: local!value,
      style: "STRONG",
      size: a!defaultValue(ri!textSize, "MEDIUM"),
      color: a!defaultValue(ri!textColor, "STANDARD")
    )
  }
)

