_a-0000ed24-7935-8000-9d2d-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!completedTasks,
  expression: a!match(
    value: fv!item[recordType!CMGT_Task.taskBehaviorTypeId],
    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
    then: a!map(
      task: fv!item,
      selectedResponseOptionUuid: fv!item[recordType!CMGT_Task.selectedTaskResponseOption.taskResponseOptionUuid],
      satisfiedConditionSetUuid: null
    ),
    whenTrue: and(
      fv!value = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
      fv!item[recordType!CMGT_Task.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
    ),
    then: a!localVariables(
      local!conditionsMet: #"SYSTEM_SYSRULES_forEach"(
        items: a!flatten(
          fv!item[recordType!CMGT_Task.conditionSet]
        ),
        expression: rule!CMGT_UTIL_RejectBlank(
          a!localVariables(
            local!isMetOfConditions: index(
              a!flatten(
                index(
                  fv!item,
                  recordType!CMGT_ConditionSet.condition,
                  null()
                )
              ),
              recordType!CMGT_Condition.isMet,
              null()
            ),
            if(
              a!match(
                value: fv!item[recordType!CMGT_ConditionSet.operatorCode],
                equals: cons!CMGT_TXT_SET_OPERATOR_OR,
                then: or(local!isMetOfConditions),
                equals: cons!CMGT_TXT_SET_OPERATOR_AND,
                then:  and(
                  rule!CMGT_UTIL_IsNotBlank(local!isMetOfConditions),
                  not(contains(local!isMetOfConditions, false))
                ),
                default: false()
              ),
              fv!item,
              null()
            )
          )
        )
      ),
      local!satisfiedConditionSetUuids: index(
        local!conditionsMet,
        recordType!CMGT_ConditionSet.conditionSetUuid,
        null()
      ),
      local!automationStartConditionUuid: union(
        local!satisfiedConditionSetUuids,
        local!satisfiedConditionSetUuids
      ),
      a!map(
        task: fv!item,
        selectedResponseOptionUuid: null,
        /*a!defaultValue is added to handle the Default Condition Set. Else, Empty Array gets constructed.*/
        satisfiedConditionSetUuid: a!defaultValue(
          value: local!automationStartConditionUuid,
          default: null()
        )
      )
    ),
    /*Default value can be added if needed in future*/
    default: {}
  )
)

