                          )
                        },

                      ),
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {},
                        width:if(#"SYSTEM_SYSRULES_isPageWidth"({"DESKTOP_WIDE","DESKTOP_WIDE"}) ,"NARROW_PLUS","EXTRA_NARROW"),
                        showWhen: local!isWide
                      ),

                    },
                    stackWhen: { "PHONE", "TABLET_PORTRAIT" }
                  )
                },
                height: "AUTO",
                style: "TRANSPARENT",
                padding: "MORE",
                marginBelow: "STANDARD",
                showBorder: false(),
                showShadow: false()
              )
            }

          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Space in the left hand side*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!CMGT_LA_HomePage_FAQ(brandingMap: local!brandingMap),

          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          )
        }
      )
    },
    brandingMap: local!brandingMap,
    height:"SHORT_PLUS"
  )
)

_a-0000ecbe-e702-8000-9cf8-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  ri!automationTaskUuids,
  a!localVariables(
    local!existingMap: rule!CMGT_UTIL_Filter(
      records: ri!automationEvaluationLoopExecutions,
      field: "taskUUID",
      value: fv!item,
      filterType: "FULL_RESULT"
    ),
    if(
      local!existingMap.matchCount = 0,
      append(
        ri!automationEvaluationLoopExecutions,
        a!map(taskUUID: fv!item, numOfExec: 1)
      ),
      #"urn:appian:function:v1:a:update"(
        ri!automationEvaluationLoopExecutions,
        local!existingMap.matchIndices,
        a!map(
          taskUUID: local!existingMap.matches.taskUUID,
          numOfExec: local!existingMap.matches.numOfExec[1] + 1
        )
      )
    )
  )
)

_a-0000e9fa-aa45-8000-9bda-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!user,
  expression: a!localVariables(
    local!currentUserRecord: if(
      isusernametaken(fv!item),
      rule!CMGT_UTIL_Filter(
        records: ri!userRecord,
        field: #"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_username",
        value: fv!item
      ),
      null()
    ),
    cast(
      'type!{http://www.appian.com/ae/types/2009}Text',
      if(
        rule!CMGT_UTIL_IsNotBlank(input: local!currentUserRecord),
        if(
          or(
            local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_isServiceAccount"] = true,
            #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
              username: fv!item,
              groups: cons!CMGT_GRP_SERVICE_ACCOUNTS
            )
          ),
          cons!CMGT_TXT_AUTOMATED_SYSTEM,
          local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName"]
        ),
        if(
          isnull(fv!item),
          if(
            ri!returnNullForEmpty,
            null,
            cons!CMGT_TXT_EMPTY_VALUE
          ),
          fv!item
        )
      )
    )
  )
)

_a-0000e9fd-9441-8000-9bdc-011c48011c48_.sail_code:
a!localVariables(
  local!details: cast(
    'type!{http://www.appian.com/ae/types/2009}Map',
    if(
      a!isNotNullOrEmpty(
        ri!data[recordType!CMGT_EventHistory_Entity.details]
      ),
      #"SYSTEM_SYSRULES_fromJson_v1"(
        ri!data[recordType!CMGT_EventHistory_Entity.details]
      ),
      null
    )
  ),
  a!match(
    value: ri!data[recordType!CMGT_EventHistory_Entity.entityEventTypeId],
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_ENTITY_ADD_RELATED_ENTITY,
        cons!CMGT_REFID_EVENT_TYPE_ENTITY_REMOVE_RELATED_ENTITY
      },
      fv!value
    ),
    then: concat(
      rule!CMGT_QR_GetEntity(
        entityId: local!details.relatedEntityId,
        returnType: "SINGLE_OBJECT"
      )[recordType!CMGT_Entity.title],
      char(10),
      #"urn:appian:translation-string:v1:802ce84a-8b69-44d9-acb6-b3f80a4fc7b7"(
        #"urn:appian:translation-variable:v1:00cdd306-d8cf-499b-8b48-5ca77d333e58": displayvalue(
          tointeger(local!details.relationshipTypeId),
          ri!relationshipMapTypes[recordType!CMGT_CFG_RelationshipMapType.relationshipMapTypeId],
          ri!relationshipMapTypes[recordType!CMGT_CFG_RelationshipMapType.relationship.label],
          null
        )
      )
    ),
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_ENTITY_ADD_RELATED_CASE,
        cons!CMGT_REFID_EVENT_TYPE_ENTITY_REMOVE_RELATED_CASE
      },
      fv!value
    ),
    then: a!localVariables(
      local!case: rule!CMGT_QR_GetCase(
        returnType: "SINGLE_OBJECT",
        caseId: local!details.caseId
      ),
      concat(
        local!case[recordType!CMGT_Case.friendlyId],
        " | ",
        local!case[recordType!CMGT_Case.title]
      )
    ),
    default: local!details.value
  )
)

_a-0000ed12-0330-8000-9d27-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Integer?list',
  with(
    local!array: a!flatten(ri!array),

    if(

      /*Have to handle separately for document types due to how it casts to string*/
      rule!CMGT_UTIL_TypeCompare(
        value1: local!array,
        value2: {todocument(null)}
      ),
      wherecontains(
        todocument(ri!values),
        local!array
      ),

      /*Have to handle separately for group types due to how it casts to string*/
      rule!CMGT_UTIL_TypeCompare(
        value1: local!array,
        value2: {togroup(null)}
      ),
      wherecontains(
        togroup(ri!values),
        local!array
      ),

      /*Casting to string accounts for any primitive type (integer, boolean, ect.)*/
      wherecontains(
        if(
          rule!CMGT_UTIL_IsBlank(ri!values),
          touniformstring(ri!values),
          touniformstring(a!flatten(ri!values))
        ),
        touniformstring(local!array)
      )
    )
  )
)

