_a-0000e9ae-873e-8000-9baf-011c48011c48_.sail_code:
/*
The grid will dynamically show columns based on the passed in context.

Currently the context include:
"EXPANDED_VIEW",
"CASE_SUMMARY",
"COMPLETED_TASK",
"MY_TASK_VIEW",
"ABBREVIATED_VIEW",
"MY_SHARED_QUEUE"

Search for the specific context to find what will be displayed or use the context as the passed in rule input
*/
#"SYSTEM_SYSRULES_gridField_v3"(
  label: ri!label,
  labelPosition: "COLLAPSED",
  emptyGridMessage: if(
    contains(
      { "ABBREVIATED_VIEW", "EXPANDED_VIEW" },
      ri!context
    ),
    #"urn:appian:translation-string:v1:6315d6b5-8590-4305-abf2-a55a5213c025",
    #"urn:appian:translation-string:v1:9541cbd3-719b-4809-bfdc-ee599f8cbab7"
  ),
  rowHeader: if(ri!milestonesExist, 2, 1),
  data: #"SYSTEM_SYSRULES_recordData"(
    recordType: recordType!CMGT_Task,
    filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      logicalExpressions: ri!logicalExpression,
      filters: {
        ri!filters,
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Task.isActive,
          operator: "=",
          value: true
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Task.isCurrent,
          operator: "=",
          value: true
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Task.taskIntentionCode,
          operator: "=",
          value: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_WORKFLOW
        )
      },
      ignoreFiltersWithEmptyValues: true
    ),
    fields: {
      recordType!CMGT_Task.title,
      recordType!CMGT_Task.taskInstanceUuid,
      recordType!CMGT_Task.taskResponseOptionId,
      recordType!CMGT_Task.selectedTaskResponseOption,
      recordType!CMGT_Task.taskResponseOptions,
      recordType!CMGT_Task.dueOn,
      recordType!CMGT_Task.isCompleted,
      recordType!CMGT_Task.taskBehaviorTypeId,
      recordType!CMGT_Task.taskBehaviorType.isAutomated,
      recordType!CMGT_Task.taskBehaviorType.label,
      recordType!CMGT_Task.taskBehaviorType.icon,
      recordType!CMGT_Task.statusId,
      recordType!CMGT_Task.status,
      recordType!CMGT_Task.status.icon,
      recordType!CMGT_Task.status.accentColor,
      recordType!CMGT_Task.status.statusLabel,
      recordType!CMGT_Task.caseId,
      recordType!CMGT_Task.case.friendlyId,
      recordType!CMGT_Task.case.title,
      recordType!CMGT_Task.roles.username,
      recordType!CMGT_Task.roles,
      recordType!CMGT_Task.roles.groupAssignee,
      recordType!CMGT_Task.milestone.label,
      recordType!CMGT_Task.milestone.milestoneColor,
      recordType!CMGT_Task.modifiedOn,
      recordType!CMGT_Task.modifiedBy,
      recordType!CMGT_Task.modifiedByUser,
      recordType!CMGT_Task.completedOn,
      recordType!CMGT_Task.completedBy,
      recordType!CMGT_Task.completedByUser,
      recordType!CMGT_Task.instanceCount
    }
  ),
  columns: {
    /*milestone for abbreviated view*/
    #"SYSTEM_SYSRULES_gridColumn"(
      sortField: recordType!CMGT_Task.milestone.label,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: if(
          a!isNullOrEmpty(
            value: fv!row[recordType!CMGT_Task.milestone.label]
          ),
          "",
          {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "circle",
              caption: if(
                ri!context = "ABBREVIATED_VIEW",
                #"urn:appian:translation-string:v1:802692c1-f167-44d0-8058-196440edbb6a" & " - ",
                null
              ) & fv!row[recordType!CMGT_Task.milestone.label],
              color: fv!row[recordType!CMGT_Task.milestone.milestoneColor],
              size: "STANDARD",
              altText: if(
                ri!context = "ABBREVIATED_VIEW",
                null,
                #"urn:appian:translation-string:v1:802692c1-f167-44d0-8058-196440edbb6a"
              )
            )
          }
        )
      ),
      showWhen: and(
        contains({ "ABBREVIATED_VIEW" }, ri!context),
        ri!milestonesExist
      ),
      align: "CENTER",
      width: "ICON"
    ),
    /*Title*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:fe962919-107c-4fbe-b95f-53eef9152c9f",
      sortField: recordType!CMGT_Task.title,
      value: a!localVariables(
        /*Logic to calculate the number of dependents and display them */
        local!afterTaskBranchIndices: if(
          rule!CMGT_UTIL_IsNotBlank(ri!taskBranchMaps),
          rule!CMGT_UTIL_Filter(
            records: ri!taskBranchMaps.afterTaskBranches,
            fieldValuePairs: {
              a!map(
                field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
                value: fv!row[recordType!CMGT_Task.taskInstanceUuid]
              ),
              a!map(
                field: recordType!CMGT_TaskBranch.isActive,
                value: { true, null }
              )
            },
            filterType: "MATCH_INDICES"
          ),
          {}
        ),
        local!afterTasks: if(
          rule!CMGT_UTIL_IsNotBlank(local!afterTaskBranchIndices),
          rule!CMGT_UTIL_Filter(
            records: ri!tasks,
            field: recordType!CMGT_Task.taskInstanceUuid,
            value: index(
              a!flatten(ri!taskBranchMaps.afterTaskBranches)[local!afterTaskBranchIndices],
              recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
              null
            )
          ),
          {}
        ),
        local!dependents: if(
          a!isNotNullOrEmpty(local!afterTasks),
          joinarray(
            local!afterTasks[recordType!CMGT_Task.title],
            ", "
          ),
          {}
        ),
        local!countOfDependents: if(
          a!isNotNullOrEmpty(local!dependents),
          length(local!afterTasks),
          0
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                fv!row[recordType!CMGT_Task.title]
              },
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!CMGT_Task,
                identifier: fv!identifier
              ),
              linkStyle: "STANDALONE",
              color: ri!brandingMap.StandardTextColor,
              style: "STRONG"
            ),
            if(
              tointeger(
                fv!row[recordType!CMGT_Task.instanceCount]
              ) > 1,
              {
                char(32),
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "refresh",
                  size: "SMALL",
                  caption: if(
                    tointeger(
                      fv!row[recordType!CMGT_Task.instanceCount]
                    ) - 1 > 1,
                    #"urn:appian:translation-string:v1:64d9fea3-366e-42fa-98e7-d799c8a56c28"(
                      #"urn:appian:translation-variable:v1:bf400cdc-071c-434e-9ccf-a0b2dd2c0277": tointeger(
                        fv!row[recordType!CMGT_Task.instanceCount]
                      ) - 1
                    ),
                    #"urn:appian:translation-string:v1:fa272fac-20b3-41b4-aff4-4e2395054003"
                  ),
                  color: ri!brandingMap.SecondaryTextColor
                ),
                char(32),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: tointeger(
                    fv!row[recordType!CMGT_Task.instanceCount]
                  ) - 1,
                  size: "SMALL",
                  color: ri!brandingMap.SecondaryTextColor
                )
              },
              {}
            ),
            char(10),
            /*Task behavior type*/
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: fv!row[recordType!CMGT_Task.taskBehaviorType.icon],
              size: "SMALL",
              color: if(
                tointeger(
                  fv!row[recordType!CMGT_Task.taskBehaviorType.taskBehaviorTypeId]
                ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                ri!brandingMap.AccentColor,
                ri!brandingMap.SecondaryTextColor
              )
            ),
            char(32),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: fv!row[recordType!CMGT_Task.taskBehaviorType.label],
                  color: if(
                    tointeger(
                      fv!row[recordType!CMGT_Task.taskBehaviorType.taskBehaviorTypeId]
                    ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                    ri!brandingMap.AccentColor,
                    ri!brandingMap.SecondaryTextColor
                  ),
                  size: "SMALL"
                )
              },
              showWhen: contains(
                {
                  "EXPANDED_VIEW",
                  "CASE_SUMMARY",
                  "COMPLETED_TASK",
                  "MY_TASK_VIEW",
                  "MY_SHARED_QUEUE",
                  "ABBREVIATED_VIEW"
                },
                ri!context
              )
            ),
            char(32),
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "arrow-right",
              color: ri!brandingMap.SecondaryTextColor,
              altText: #"urn:appian:translation-string:v1:6fa1bc00-eaf5-45d0-966b-16d2a592ccaf",
              size: "SMALL",
              caption: local!dependents,
              showWhen: and(
                contains(
                  { "EXPANDED_VIEW", "ABBREVIATED_VIEW" },
                  ri!context
                ),
                local!countOfDependents > 0
              )
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: {
                concat(
                  " ",
                  #"urn:appian:translation-string:v1:0b9b52cc-25f5-4444-90b3-6d70a4491713",
                  " ",
                  local!countOfDependents,
                  " ",
                  if(
                    local!countOfDependents = 1,
                    #"urn:appian:translation-string:v1:f840e77d-0aae-4eeb-b795-0394d8bff5b4",
                    #"urn:appian:translation-string:v1:7178a955-6103-460e-8886-24c185dc3faf"
                  )
                )
              },
              color: ri!brandingMap.SecondaryTextColor,
              size: "SMALL",
              showWhen: and(
                contains(
                  { "EXPANDED_VIEW", "ABBREVIATED_VIEW" },
                  ri!context
                ),
                local!countOfDependents > 0
              )
            )
          }
        )
      )
    ),
    /*Status*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:7e0ce8d9-9374-4a9e-925a-fc30eb5ba4b6",
      sortField: recordType!CMGT_Task.status.statusLabel,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: fv!row[recordType!CMGT_Task.status.icon],
            caption: if(
              and(
                fv!row[recordType!CMGT_Task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                fv!row[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_COMPLETED
              ),
              fv!row[recordType!CMGT_Task.selectedTaskResponseOption.label],
              ""
            ),
            color: rule!CMGT_UTIL_DisplayBranding(
              brandingMap: ri!brandingMap,
              key: fv!row[recordType!CMGT_Task.status.accentColor]
            )
          ),
          " ",
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: fv!row[recordType!CMGT_Task.status.statusLabel]
          )
        },
        preventWrapping: true()
      ),
      showWhen: contains(
        {
          "EXPANDED_VIEW",
          "MY_TASK_VIEW",
          "MY_SHARED_QUEUE",
          "CASE_SUMMARY",
          "ABBREVIATED_VIEW"
        },
        ri!context
      )
    ),
    /*Milestone*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:c29825ed-83d5-4ac7-9f29-775ca7c372a9",
      sortField: recordType!CMGT_Task.milestone.label,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: if(
          a!isNullOrEmpty(
            value: fv!row[recordType!CMGT_Task.milestone.label]
          ),
          cons!CMGT_TXT_EMPTY_VALUE,
          {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "circle",
              color: fv!row[recordType!CMGT_Task.milestone.milestoneColor],
              size: "STANDARD"
            ),
            " ",
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: fv!row[recordType!CMGT_Task.milestone.label]
            )
          }
        )
      ),
      showWhen: contains({ "EXPANDED_VIEW" }, ri!context)
    ),
    /*Case*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: #"urn:appian:translation-string:v1:b5c1a7f9-aa4e-4105-b1e3-6bf63eef7ce7",
      value: #"SYSTEM_SYSRULES_tagField"(
        tags: #"SYSTEM_SYSRULES_tagItem_v1"(
          text: fv!row[recordType!CMGT_Task.case.friendlyId] & " | " & fv!row[recordType!CMGT_Task.case.title],
          backgroundColor: ri!brandingMap.TagBackgroundColor,
          textColor: ri!brandingMap.TagTextColor,
          link: #"SYSTEM_SYSRULES_recordLink"(
            recordType: recordType!CMGT_Case,
            identifier: fv!row[recordType!CMGT_Task.caseId]
          )
        )
      ),
      showWhen: contains(
        { "MY_TASK_VIEW", "MY_SHARED_QUEUE" },
        ri!context
      )
    ),
    /*Assignee*/
    #"SYSTEM_SYSRULES_gridColumn"(
      label: if(
        contains({ "MY_SHARED_QUEUE" }, ri!context),
        #"urn:appian:translation-string:v1:559ce3f6-e852-4bee-b5b2-ca315e1ebbef",
        #"urn:appian:translation-string:v1:7bcb0001-ac03-470f-a26b-be3786709f7e"
      ),
      value: rule!CMGT_UTIL_DisplayUserAndGroupAssigneeList(
        separator: ", ",
        emptyLabel: #"urn:appian:translation-string:v1:83cc28fc-8558-4039-804c-602530960d97",
        textSize: if(
          contains({ "MY_SHARED_QUEUE" }, ri!context),
          "STANDARD",
          "SMALL"
