    - WITH a populated dueOn
    - ASSSIGNED to the logged in user via userAssignee
  */
  local!caseLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.dueOn,
        operator: "not null"
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.statusId,
        operator: "in",
        value: {
          cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
          cons!CMGT_REFID_STATUS_CASE_OPEN
        }
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.roles.userAssignee,
        operator: "=",
        value: ri!loggedInUser
      )
    }
  ),
  local!taskLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
