a!localVariables(
  local!seperator: if(
    ri!showInSeperateLine,
    char(10),
    char(32)
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Task Identitifer"),
    labelPosition: "COLLAPSED",
    value: {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!nameLabel & char(10),
        showWhen: a!isNotNullOrEmpty(ri!nameLabel),
        size: "MEDIUM"
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: concat(
          #"urn:appian:translation-string:v1:47c0f678-c7ec-47ad-ac82-1751a615f89c"(
            #"urn:appian:translation-variable:v1:9162e0ba-fea7-496d-8944-cd3d29902607": rule!CMGT_UTIL_FormatDateTime(
              dateTime: ri!lastUpdatedTimestamp,
              includeTime: false
            )
          ),
          char(10),
          char(10)
        ),
        showWhen: a!isNotNullOrEmpty(ri!lastUpdatedTimestamp),
        color: ri!brandingMap.SecondaryTextColor,

      ),
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: ri!taskBehaviorType[recordType!CMGT_CFG_TaskBehaviorType.icon],
        color: ri!brandingMap.StandardTextColor,
        size: "STANDARD",
        showWhen: ri!showTaskBehaviorTypeIcon
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: char(32),
        showWhen: ri!showTaskBehaviorTypeIcon
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          ri!taskBehaviorType[recordType!CMGT_CFG_TaskBehaviorType.label]
        },
        color: if(
          tointeger(ri!taskBehaviorType[recordType!CMGT_CFG_TaskBehaviorType.taskBehaviorTypeId]) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
          ri!brandingMap.InfoAccentColor,
          ri!brandingMap.StandardTextColor
        ),
        size: "SMALL"
      ),
      local!seperator,
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "arrow-right",
        color: ri!brandingMap.SecondaryTextColor,
        altText: #"urn:appian:translation-string:v1:6fa1bc00-eaf5-45d0-966b-16d2a592ccaf",
        size: "SMALL",
        caption: ri!dependents,
        showWhen: and(
          ri!showDependents,
          ri!countOfDependents > 0
        )
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          " ",
          concat(
            #"urn:appian:translation-string:v1:0b9b52cc-25f5-4444-90b3-6d70a4491713",
            " ",
            ri!countOfDependents,
            " ",
            if(
              ri!countOfDependents = 1,
              #"urn:appian:translation-string:v1:f840e77d-0aae-4eeb-b795-0394d8bff5b4",
              #"urn:appian:translation-string:v1:7178a955-6103-460e-8886-24c185dc3faf"
            )
          ),
          " "
        },
        color: ri!brandingMap.SecondaryTextColor,
        size: "SMALL",
        showWhen: and(
          ri!showDependents,
          ri!countOfDependents > 0
        )
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: {
          if(
            ri!showSlaDays,
            {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "clock-o",
                altText: #"urn:appian:translation-string:v1:9f41e6f7-45b5-4350-af35-58bf2b3af4eb"
              ),
              " ",
              {
                /*Adding conditions to check whether the value is 0,If so consider as empty*/
                if(
                  rule!CMGT_UTIL_isEmptySLA(ri!slaDays),
                  cons!CMGT_TXT_EMPTY_VALUE,
                  {
                    rule!CMGT_WFL_FormatSLADisplay(slaMap: ri!slaDays),

                  }
                ),

              },
              " ",

            },
            {}
          ),
          if(
            a!defaultValue(ri!showGroupAssignee, true),
            {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "group",
                altText: #"urn:appian:translation-string:v1:64bf7660-8738-4162-81c8-166d776a7324"
              ),
              " ",
              if(
                rule!CMGT_UTIL_IsBlank(ri!defaultGroupAssignee),
                cons!CMGT_TXT_EMPTY_VALUE,
                group(ri!defaultGroupAssignee, "groupName")
              )
            },
            {}
          )
        },
        color: ri!brandingMap.SecondaryTextColor,
        size: "SMALL"
      )
    },
    marginBelow: "NONE"
  )
)

_a-0000ee70-a572-8000-9d9f-011c48011c48_.sail_code:
a!localVariables(
  local!searchTerm,
  local!searchTermTemp,
  local!showUnpublished: true(),
  local!isCurrentLevelAllCases: tostring(ri!currentLevelSelected) = #"urn:appian:translation-string:v1:3807a409-6086-4767-8e1b-dd26b980ff5f",
  /*Query to Handle the BatchSize of Custom Paging*/
  local!allAvailableResources: rule!CMGT_KB_QR_GetResources(
    returnType: "OBJECT_ARRAY",
    triggerRefresh: ri!triggerRefresh,
    searchTerm: local!searchTerm,
    fields: {
      recordType!CMGT_KB_Resource.document,
      recordType!CMGT_KB_Resource.caseType
    },
    searchFields: {
      recordType!CMGT_KB_Resource.title,
      recordType!CMGT_KB_Resource.resourceText.plainText,
      recordType!CMGT_KB_Resource.modifiedBy,
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/20381804-6324-459c-8688-d93a850ce2ab/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_name",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_description"
    },
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_KB_Resource.status,
        operator: "=",
        value: #"urn:appian:translation-string:v1:c46d69c3-d638-48ac-abee-bfc4414a30cf",
        applyWhen: toboolean(local!showUnpublished) <> true()
      )
    },
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "OR",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "is null"
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "in",
          value: {
            ri!selectedCaseTypeUuid,
            ri!selectedCaseType[recordType!CMGT_CFG_CaseType.parentUuid]
          }
        )
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
