          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(marginBelow: "EVEN_MORE"),
            rule!CMGT_UTIL_DisplayEmptyState(
              brandingMap: ri!brandingMap,
              primaryText: #"urn:appian:translation-string:v1:21d9dfbb-38eb-494f-a759-2c575e3f8b2c",
              docImage: cons!CMGT_DOC_IMAGES_NO_RESULTS_ICON,
              imageSize: "SMALL",
              primaryTextSize: "MEDIUM"
