  items: ri!relatedCaseMaps,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_RelatedCaseMap.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_RelatedCaseMap.modifiedBy,
        value: ri!initiator
      )
    },
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_RelatedCaseMap.createdOn,
        value: now()
      ),
      a!map(
