_a-0000ecb5-acf8-8000-9cf6-011c48011c48_.sail_code:
a!localVariables(
  local!distinctBeforeTaskUUID: rule!CMGT_UTIL_Distinct(
    ri!caseTypeWorkflowTaskBranch[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid]
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!distinctBeforeTaskUUID,
          expression: a!localVariables(
            local!thisUUID: fv!item,
            local!thisTaskBranches: rule!CMGT_UTIL_Filter(
              records: ri!caseTypeWorkflowTaskBranch,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                  value: local!thisUUID
                ),

              }
            ),
            {
              a!match(
                value: tostring(
                  rule!CMGT_UTIL_Filter(
                    records: ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
                    value: local!thisUUID
                  )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.actionCode]
                ),
                equals: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
                /* Grouping the branches with same Condition Set Uuids */
                then: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!CMGT_UTIL_Distinct(
                    local!thisTaskBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid]
                  ),
                  expression: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                    taskUUID: local!thisUUID,
                    conditionSetUUID: fv!item,
                    afterCaseTypeWorkflowTaskBranches: rule!CMGT_UTIL_Filter(
                      records: local!thisTaskBranches,
                      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid,
                      value: fv!item,

                    ),
                    isRequired: true,
                    isActive: true,
                    isRequirednessValid: true
                  )
                ),
                equals: cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
                /*   Grouping all the branches as the send email dependents are always required and valid      */
                then: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                  taskUUID: local!thisUUID,
                  afterCaseTypeWorkflowTaskBranches: local!thisTaskBranches,
                  isRequired: true,
                  isActive: true,
                  isRequirednessValid: true
                ),
                default: {}
              )
            }
          )
        )
      )
    )
  )
)

