a!localVariables(
  local!precedentEdges: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: ri!allEdges,
      field: "to",
      value: ri!currentNode.id
    ),
    refreshAlways: true
  ),
  local!precedentNodes: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: ri!orderedNodes,
      field: "id",
      value: local!precedentEdges.from
    ),
    refreshAlways: true
  ),
  /*setting this as a percent 1-100*/
  /*if there are no parents the score is 50*/
  local!averageParentHorizontalOrder: a!refreshVariable(
    value: if(
      a!isNullOrEmpty(local!precedentNodes),
      50.0,
      average(local!precedentNodes.horizontalOrder)
    ),
    refreshAlways: true
  ),
  #"urn:appian:function:v1:a:update"(
    data: ri!currentNode,
    index: "horizontalOrder",
    value: local!averageParentHorizontalOrder
  )
)

