_a-0000e990-c462-8000-9ba7-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!documents,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    alwaysOverwrite: {
      /*ri!taskCompletedOn will be removed once related record tree updation feature is released which will automatically update these metadata with parent's data*/
      /*ri!taskCompletedOn is added, so that in the frontend we can display documents based on task completed timestamp in task summary*/
      a!map(
        field: recordType!CMGT_Document.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_Document.modifiedBy,
        value: ri!initiator
      )
    },
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_Document.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_Document.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_Document.isActive,
        value: true
      ),
      a!map(
        field: recordType!CMGT_Document.isPrivate,
        value: #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
          ri!initiator,
          cons!CMGT_GRP_INTERNAL_USERS
        )
      ),
      a!map(
        field: recordType!CMGT_Document.documentName,
        value: document(
          fv!item[recordType!CMGT_Document.appianDocId],
          "name"
        )
      ),
      a!map(
        field: recordType!CMGT_Document.extension,
        value: document(
          fv!item[recordType!CMGT_Document.appianDocId],
          "extension"
        )
      ),
      a!map(
        field: recordType!CMGT_Document.size,
        value: document(
          fv!item[recordType!CMGT_Document.appianDocId],
          "size"
        )
      ),
      /*Set caseId/taskId in case it's on the object and not the FK field*/
      a!map(
        field: recordType!CMGT_Document.caseId,
        value: fv!item[recordType!CMGT_Document.case.caseId]
      ),
      a!map(
        field: recordType!CMGT_Document.taskId,
        value: fv!item[recordType!CMGT_Document.task.taskId]
      )
    }
  )
)

