_a-0000ea13-fced-8000-9be9-011c48011c48_.sail_code:
a!match(
  value: ri!context,
  whenTrue: rule!CMGT_UTIL_IsBlank(
    ri!roles
  ),
  then: {},
  equals: "CASE",
  then: #"SYSTEM_SYSRULES_forEach"(
    items: ri!roles,
    expression: rule!CMGT_CaseEventHistory_Constructor(
      caseId: fv!item[recordType!CMGT_Role.caseId],
      initiator: ri!initiator,
      eventTypeId: a!match(
        value: fv!item[recordType!CMGT_Role.roleTypeId],
        equals: cons!CMGT_SELF_REFID_ROLE_TYPE_CLAIMANT,
        then: cons!CMGT_SELF_REFID_EVENT_TYPE_CASE_ADD_CLAIMANT,
        equals: cons!CMGT_REFID_ROLE_TYPE_WATCHER,
        then: if(
          fv!item[recordType!CMGT_Role.isActive],
          cons!CMGT_REFID_EVENT_TYPE_CASE_ADD_WATCHER,
          cons!CMGT_REFID_EVENT_TYPE_CASE_REMOVED_WATCHER
        ),
        equals: cons!CMGT_LA_REFID_ROLE_TYPE_SUBMITTER,
        then: cons!CMGT_LA_REFID_EVENT_TYPE_CASE_ADD_SUBMITTER,
        whenTrue: or(
          fv!value = cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
          fv!value = cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
        ),
        then: if(
          fv!item[recordType!CMGT_Role.isActive],
          cons!CMGT_REFID_EVENT_TYPE_CASE_ADD_ASSIGNEE,
          cons!CMGT_REFID_EVENT_TYPE_CASE_REMOVED_ASSIGNEE
        ),
        default: {}
      ),
      details: a!map(
        user: tostring(
          fv!item[recordType!CMGT_Role.username]
        ),
        groupId: tointeger(
          fv!item[recordType!CMGT_Role.groupId]
        )
      )
    )
  ),
  equals: "TASK",
  then: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!roles,
      expression: a!localVariables(
        local!originalRole: rule!CMGT_UTIL_Filter(
          records: ri!originalRoles,
          field: recordType!CMGT_Role.roleId,
          value: fv!item[recordType!CMGT_Role.roleId]
        ),
        if(
          /*new role or change in active flag, create event*/
          or(
            a!isNullOrEmpty(
              local!originalRole
            ),
            and(
              a!isNotNullOrEmpty(
                local!originalRole
              ),
              local!originalRole[recordType!CMGT_Role.isActive] <> fv!item[recordType!CMGT_Role.isActive]
            )
          ),
          rule!CMGT_TaskEventHistory_Constructor(
            taskId: fv!item[recordType!CMGT_Role.taskId],
            initiator: ri!initiator,
            eventTypeId: if(
              fv!item[recordType!CMGT_Role.isActive],
              cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ASSIGNEE,
              cons!CMGT_REFID_EVENT_TYPE_TASK_REMOVED_ASSIGNEE
            ),
            details: a!map(
              user: tostring(
                fv!item[recordType!CMGT_Role.username]
              ),
              groupId: tointeger(
                fv!item[recordType!CMGT_Role.groupId]
              )
            )
          ),
          {}
        )
      )
    )
  ),
  default: {}
)

