a!localVariables(
  local!noOfResourcesInCurrentPage: length({ ri!resourcesToDisplay }),
  local!noOfAlignmentCards: abs(
    ri!batchSize - local!noOfResourcesInCurrentPage
  ),
  if(
    rule!CMGT_UTIL_IsNotBlank(input: ri!resourcesToDisplay),
    {
      /*Heading*/
      #"SYSTEM_SYSRULES_headingField"(
        text: #"urn:appian:translation-string:v1:aba40cfc-a3ef-4d2b-bcaf-4862d56a0e33"(
          #"urn:appian:translation-variable:v1:0aa82461-056b-44c3-834a-141269ffb0c4": ri!hierarchyLevel
        ),
        marginBelow: "NONE",
        marginAbove: "NONE",
        size: "SMALL",
        fontWeight: ri!brandingMap.SecondaryHeaderFontWeight,
        color: ri!brandingMap.StandardTextColor
      ),
      /*Resource Cards*/
      #"SYSTEM_SYSRULES_cardGroupLayout"(
        spacing: "DENSE",
        marginAbove: "LESS",
        marginBelow: if(
          ri!numberOfResources > ri!batchSize,
          "EVEN_LESS",
          "MORE"
        ),
        cards: {
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!resourcesToDisplay,
            expression: rule!CMGT_KB_SingleResourceDisplay(
              resource: fv!item,
              brandingMap: ri!brandingMap,
              selectedResource: ri!selectedResource
            )
          ),
          /* Added these cards for alignment */
          if(
            local!noOfAlignmentCards = 0,
            {},
            #"SYSTEM_SYSRULES_forEach"(
              items: enumerate(local!noOfAlignmentCards),
              expression: #"SYSTEM_SYSRULES_cardLayout"(
                showBorder: false(),
                padding: "LESS",
                height: "AUTO",
                style: "TRANSPARENT"
              )
            )
          )
        },
        fillContainer: true,
        cardHeight: "AUTO"
      ),
      /*Pagination*/
      if(
        ri!numberOfResources > ri!batchSize,
        rule!CMGT_UTIL_CustomPaging(
          pagingInfo: ri!pagingInfo,
          totalCount: ri!numberOfResources,
          brandingMap: ri!brandingMap
        ),
        {}
      )
    },
    {}
  )
)

