          whenTrue: rule!CMGT_UTIL_IsNotBlank(local!selectedResource),
          then: rule!CMGT_KB_DisplayResource(
            selectedResource: local!selectedResource,
            brandingMap: local!brandingMap,
            triggerRefresh: local!triggerRefresh
          ),
          whenTrue: local!allAvailableResourcesCount = 0,
