                primaryText: #"urn:appian:translation-string:v1:8d090952-9d52-491e-9218-3c1e254b9d43",
                secondaryText: #"urn:appian:translation-string:v1:34257d03-f106-4a39-9df7-9df147ead34f",
                docImage: cons!CMGT_KB_DOC_IMAGES_KNOWLEDGE_BASE_EMPTY_STATE,
                additionalContent: #"SYSTEM_SYSRULES_recordActionField_v1"(
                  actions: if(
                    a!isNotNullOrEmpty(ri!selectedCaseTypeUuid),
                    /*Add resource record action*/
                    #"SYSTEM_SYSRULES_recordActionItem"(
                      action: #"urn:appian:record-action:v1:82aa3b28-94c2-4d3c-a403-bb813bd76155/faa27aab-0728-4c54-a932-daa98dc7da5b",
                      identifier: ri!selectedCaseTypeUuid
                    ),
                    #"SYSTEM_SYSRULES_recordActionItem"(
                      action: #"urn:appian:record-action:v1:462e1408-1c19-4591-b946-fce9233909ce/42eaf4ed-12b3-4919-88b9-8f34e6a65d3d"
                    )
                  ),
                  align: "CENTER",
                  style: "TOOLBAR_PRIMARY",
                  display: "LABEL"
                ),
                primaryTextStyle: "STRONG",
                imageSize: "EXTRA_SMALL",
                primaryTextSize: "MEDIUM",
                secondaryTextMarginBelow: "MORE"
              )
            },
            height: "EXTRA_TALL",
            padding: "EVEN_MORE",
            marginAbove: "EVEN_MORE",
            showBorder: false,
            shape: local!brandingMap.CardShape,
            showShadow: local!brandingMap.CardShowShadow,
            style: local!brandingMap.CardBackgroundColor
          ),
          /*Display Resources*/
          default: rule!CMGT_KB_ResourcesCardDisplayWrapper(
            brandingMap: local!brandingMap,
            triggerRefresh: local!triggerRefresh,
            displayConfigs: local!displayConfigs,
            currentLevelSelected: local!currentLevelSelected,
            selectedResource: local!selectedResource,
            selectedCaseTypeUuid: ri!selectedCaseTypeUuid,
            selectedCaseType: local!selectedCaseType
          )
        )
      },
      backgroundColor: local!brandingMap.DefaultBackgroundColor
    )
  )
)

