_a-0000e990-c462-8000-9ba7-011c48011c48_.sail_code:
a!localVariables(
  local!originalTasks: if(
    or(
      rule!CMGT_UTIL_IsBlank(ri!tasks),
      ri!eventType = "TRIGGERED_AUTOMATION_ACTION",

    ),
    null,
    rule!CMGT_QR_GetTask(
      taskId: ri!tasks[recordType!CMGT_Task.taskId],
      includeInactive: true,
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        a!flatten(
          ri!tasks[recordType!CMGT_Task.taskId]
        )
      ),
      fields: {
        recordType!CMGT_Task.roles,
        recordType!CMGT_Task.conditionSet
      }
    )
  ),
  rule!CMGT_Task_SetEvents(
    tasks: #"SYSTEM_SYSRULES_forEach"(
      items: ri!tasks,
      expression: a!localVariables(
        /*The current value of this record in the database*/
        local!orgItem: rule!CMGT_UTIL_Filter(
          records: local!originalTasks,
          field: recordType!CMGT_Task.taskId,
          value: fv!item[recordType!CMGT_Task.taskId]
        ),
        rule!CMGT_UTIL_SetRecordFields(
          record: fv!item,
          /*Always set these fields with each update*/
          alwaysOverwrite: {
            a!map(
              field: recordType!CMGT_Task.modifiedBy,
              value: if(
                /*we want the modified by to be process automation only when the new instance is created*/
                and(
                  rule!CMGT_UTIL_IsBlank(
                    fv!item[recordType!CMGT_Task.taskId]
                  ),
                  tointeger(
                    fv!item[recordType!CMGT_Task.instanceCount]
                  ) > 1
                ),
                cons!CMGT_TXT_PROCESS_AUTOMATION,
                ri!initiator
              )
            ),
            a!map(
              field: recordType!CMGT_Task.modifiedOn,
              value: now()
            ),
            if(
              ri!eventType = cons!CMGT_TXT_EVENT_TYPE_TASK_MODIFY_CHILD_RECORD,
              null,
              {
                a!map(
                  field: recordType!CMGT_Task.statusId,
                  value: fv!item[recordType!CMGT_Task.statusId],
                  applyWhen: contains(
                    {
                      cons!CMGT_REFID_STATUS_TASK_READY,
                      cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS
                    },
                    tointeger(
                      fv!item[recordType!CMGT_Task.statusId]
                    )
                  )
                ),
                /* Calculate dueOn based on SLA*/
                a!map(
                  field: recordType!CMGT_Task.dueOn,
                  value: if(
                    or(
                      tointeger(
                        fv!item[recordType!CMGT_Task.statusId]
                      ) = cons!CMGT_REFID_STATUS_TASK_READY,
                      tointeger(
                        fv!item[recordType!CMGT_Task.statusId]
                      ) = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS
                    ),
                    if(
                      and(
                        or(
                          rule!CMGT_UTIL_IsBlank(
                            value: local!orgItem[recordType!CMGT_Task.dueOn]
                          ),
                          tointeger(
                            local!orgItem[recordType!CMGT_Task.statusId]
                          ) = cons!CMGT_REFID_STATUS_TASK_COMPLETED,
                          tointeger(
                            local!orgItem[recordType!CMGT_Task.statusId]
                          ) = cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED
                        ),
                        /*  For open and reopen , dueOn is updated as null in the recordAction context*/
                        rule!CMGT_UTIL_IsBlank(
                          value: fv!item[recordType!CMGT_Task.dueOn]
                        ),
                        /* we should not update due on if the status is being swithced between ready and inprogress*/
                        tointeger(
                          fv!item[recordType!CMGT_Task.statusId]
                        ) <> tointeger(
                          local!orgItem[recordType!CMGT_Task.statusId]
                        ),
                        if(
                          tointeger(
                            fv!item[recordType!CMGT_Task.statusId]
                          ) = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
                          tointeger(
                            local!orgItem[recordType!CMGT_Task.statusId]
                          ) <> cons!CMGT_REFID_STATUS_TASK_READY,
                          not(
                            and(
                              tointeger(
                                local!orgItem[recordType!CMGT_Task.statusId]
                              ) = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
                              tointeger(
                                fv!item[recordType!CMGT_Task.statusId]
                              ) = cons!CMGT_REFID_STATUS_TASK_READY
                            )
                          )
                        )
                      ),
                      rule!CMGT_UTIL_CalculateDueDateBasedOnSLA(
                        SLA: fv!item[recordType!CMGT_Task.sla],
                        dateTime: now()
                      ),
                      fv!item[recordType!CMGT_Task.dueOn]
                    ),
                    fv!item[recordType!CMGT_Task.dueOn]
                  )
                ),
                /*Clear completedBy/On whenever the statusId goes back to Queued/Ready/Inprogress status*/
                a!map(
                  field: recordType!CMGT_Task.completedBy,
                  value: null,
                  applyWhen: contains(
                    {
                      cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
                      cons!CMGT_REFID_STATUS_TASK_READY,
                      cons!CMGT_REFID_STATUS_TASK_QUEUED,

                    },
                    tointeger(
                      fv!item[recordType!CMGT_Task.statusId]
                    )
                  )
                ),
                /*Task has not been completed so no date should be available*/
                a!map(
                  field: recordType!CMGT_Task.completedOn,
                  value: null,
                  applyWhen: contains(
                    {
                      cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
                      cons!CMGT_REFID_STATUS_TASK_READY,
                      cons!CMGT_REFID_STATUS_TASK_QUEUED,

                    },
                    tointeger(
                      fv!item[recordType!CMGT_Task.statusId]
                    )
                  )
                ),
                a!map(
                  field: recordType!CMGT_Task.isCurrent,
                  value: a!defaultValue(
                    fv!item[recordType!CMGT_Task.isCurrent],
                    true
                  ),

                ),
                a!map(
                  field: recordType!CMGT_Task.instanceCount,
                  value: a!defaultValue(
                    fv!item[recordType!CMGT_Task.instanceCount],
                    1
                  ),

                ),

              }
            )
          },
          /*Only modify these fields if they are currently null*/
          neverOverwrite: if(
            ri!eventType = cons!CMGT_TXT_EVENT_TYPE_TASK_MODIFY_CHILD_RECORD,
            {},
            {
              a!map(
                field: recordType!CMGT_Task.createdBy,
                value: ri!initiator
              ),
              a!map(
                field: recordType!CMGT_Task.createdOn,
                value: now()
              ),
              a!map(
                field: recordType!CMGT_Task.isActive,
                value: true
              ),
              a!map(
                field: recordType!CMGT_Task.taskUuid,
                value: rule!CMGT_UTIL_GenerateUuid_NonPlugin()
              ),
              a!map(
                field: recordType!CMGT_Task.taskInstanceUuid,
                value: rule!CMGT_UTIL_GenerateUuid_NonPlugin()
              ),
              a!map(
                field: recordType!CMGT_Task.isAdHoc,
                value: false,

              ),
              /*Default status to "Queued"*/
              a!map(
                field: recordType!CMGT_Task.statusId,
                value: cons!CMGT_REFID_STATUS_TASK_QUEUED
              ),
              /*Automatically calculate availableOn whenever the statusId is Ready*/
              a!map(
                field: recordType!CMGT_Task.availableOn,
                value: now(),
                applyWhen: tointeger(
                  fv!item[recordType!CMGT_Task.statusId]
                ) = cons!CMGT_REFID_STATUS_TASK_READY
              ),
              /*Set completedBy/On whenever the statusId matches a Completion status*/
              a!map(
                field: recordType!CMGT_Task.completedBy,
                value: ri!initiator,
                applyWhen: contains(
                  {
                    cons!CMGT_REFID_STATUS_TASK_COMPLETED,
                    cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED
                  },
                  tointeger(
                    fv!item[recordType!CMGT_Task.statusId]
                  )
                )
              ),
              a!map(
                field: recordType!CMGT_Task.completedOn,
                value: now(),
                applyWhen: contains(
                  {
                    cons!CMGT_REFID_STATUS_TASK_COMPLETED,
                    cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED
                  },
                  tointeger(
                    fv!item[recordType!CMGT_Task.statusId]
                  )
                )
              ),
              a!map(
                field: recordType!CMGT_Task.taskIntentionCode,
                value: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_WORKFLOW
              )
            }
          ),
          /*Set metadata for all child relationships*/
          childRelationships: {
            a!map(
              field: recordType!CMGT_Task.roles,
              value: rule!CMGT_Role_SetMetaData(
                roles: fv!item[recordType!CMGT_Task.roles],
                initiator: ri!initiator
              )
            ),
            a!map(
              field: recordType!CMGT_Task.comments,
              value: rule!CMGT_Comment_SetMetaData(
                /*Note that the caseId context is on task, therefore we need to set it on comment here*/
                comments: rule!CMGT_UTIL_SetForeignKey(
                  records: fv!item,
                  relationship: recordType!CMGT_Task.comments,
                  parentPkField: recordType!CMGT_Task.caseId,
                  childFkField: recordType!CMGT_Comment.caseId
                ),
                initiator: ri!initiator
              )
            ),
            a!map(
              field: recordType!CMGT_Task.documents,
              value: rule!CMGT_Document_SetMetaData(
                /*Note that the caseId context is on task, therefore we need to set it on document here*/
                documents: rule!CMGT_UTIL_SetForeignKey(
                  records: fv!item,
                  relationship: recordType!CMGT_Task.documents,
                  parentPkField: recordType!CMGT_Task.caseId,
                  childFkField: recordType!CMGT_Document.caseId
                ),
                initiator: ri!initiator
              )
            ),
            a!map(
              field: recordType!CMGT_Task.beforeTaskInstanceBranches,
              value: rule!CMGT_TaskBranch_SetMetaData(
                taskBranches: fv!item[recordType!CMGT_Task.beforeTaskInstanceBranches]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.taskResponseOptions,
              value: rule!CMGT_TaskResponseOption_SetMetaData(
                taskResponseOptions: fv!item[recordType!CMGT_Task.taskResponseOptions]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.conditionSet,
              value: rule!CMGT_ConditionSet_SetMetaData(
                conditionSets: fv!item[recordType!CMGT_Task.conditionSet],
                initiator: ri!initiator
              )
            ),
            a!map(
              field: recordType!CMGT_Task.createCaseTask,
              value: rule!CMGT_CreateCaseTask_SetMetaData(
                createCaseTask: fv!item[recordType!CMGT_Task.createCaseTask],
                initiator: ri!initiator
              ),
              applyWhen: rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.createCaseTask]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.taskCaseMap,
              value: rule!CMGT_TaskCaseMap_SetMetaData(
                taskCaseMap: fv!item[recordType!CMGT_Task.taskCaseMap],
                initiator: ri!initiator
              ),
              applyWhen: rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.taskCaseMap]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.sendEmailTask,
              value: rule!CMGT_SendEmailTask_SetMetaData(
                sendEmailTask: fv!item[recordType!CMGT_Task.sendEmailTask],
                initiator: ri!initiator
              ),
              applyWhen: rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.sendEmailTask]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.automationWorkflowTasks,
              value: rule!CMGT_Task_SetMetaData(
                tasks: fv!item[recordType!CMGT_Task.automationWorkflowTasks],
                initiator: cons!CMGT_TXT_PROCESS_AUTOMATION,
                eventType: "TRIGGERED_AUTOMATION_ACTION"
              ),
              applyWhen: rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.automationWorkflowTasks]
              )
            ),
            a!map(
              field: recordType!CMGT_Task.automationTaskAction,
              value: rule!CMGT_TaskAction_SetMetaData(
                taskAction: fv!item[recordType!CMGT_Task.automationTaskAction],
                initiator: ri!initiator
              ),
              applyWhen: rule!CMGT_UTIL_IsNotBlank(
                fv!item[recordType!CMGT_Task.automationTaskAction]
              )
            )
          }
        )
      )
    ),
    originalTasks: local!originalTasks,
    initiator: ri!initiator,
    eventType: ri!eventType,
    taskBranches: ri!taskBranches
  )
)

