/*Reference:
https://community.appian.com/w/the-appian-playbook/846/generating-uuids-guids*/
fn!lower(
  concat(
    {
      dec2hex(tointeger(rand(4) * 256), 2),
      /*8 hex digits*/
      "-",
      dec2hex(tointeger(rand(2) * 256), 2),
      /*4 hex digits*/
      "-4",
      /*version 4 = completely random*/
      dec2hex(rand(3) * 256, 1),
      /*3 hex digits*/
      "-",
      bin2hex("10" & dec2bin(rand(1) * 4, 2), 1),
      /*1 hex digit = Variant: 1st 2 bits=10=[ISO/IEC] + 2 rand bits*/
      dec2hex(rand(3) * 256, 1),
      /*3 hex digits*/
      "-",
      dec2hex(tointeger(rand(6) * 256), 2),
      /*12 hex digits*/

    }
  )
)

