    cases: #"SYSTEM_SYSRULES_forEach"(
      items: ri!cases,
      expression: rule!CMGT_UTIL_SetRecordFields(
        record: fv!item,
        alwaysOverwrite: {
          a!map(
            field: recordType!CMGT_Case.modifiedOn,
            value: now()
          ),
          a!map(
            field: recordType!CMGT_Case.modifiedBy,
            value: ri!initiator
          ),
          /* If the status is closed and the completedOn is empty then update to current time stamp, otherwise use the existing completedOn value     */
          if(
            ri!eventType = cons!CMGT_TXT_EVENT_TYPE_CASE_MODIFY_CHILD_RECORD,
            null,
            a!map(
              field: recordType!CMGT_Case.completedOn,
              value: a!match(
                value: tointeger(
                  fv!item[recordType!CMGT_Case.statusId]
                ),
                whenTrue: and(
                  fv!value = cons!CMGT_REFID_STATUS_CASE_CLOSED,
                  a!isNullOrEmpty(
                    fv!item[recordType!CMGT_Case.completedOn]
                  )
                ),
                then: now(),
                whenTrue: and(
                  fv!value <> cons!CMGT_REFID_STATUS_CASE_CLOSED,
                  a!isNotNullOrEmpty(
                    fv!item[recordType!CMGT_Case.completedOn]
                  )
                ),
                then: null,
                default: fv!item[recordType!CMGT_Case.completedOn]
              )
            ),

          )
        },
        neverOverwrite: if(
          ri!eventType = cons!CMGT_TXT_EVENT_TYPE_CASE_MODIFY_CHILD_RECORD,
          null,
          {
            a!map(
              field: recordType!CMGT_Case.isActive,
              value: true
            ),
            a!map(
              field: recordType!CMGT_Case.createdOn,
              value: now()
            ),
            a!map(
