            value: rule!CMGT_RelatedCaseMap_SetMetaData(
              relatedCaseMaps: fv!item[recordType!CMGT_Case.relatedCaseMap],
              initiator: ri!initiator
            )
          ),
          a!map(
            field: recordType!CMGT_Case.caseEntityMap,
            value: rule!CMGT_CaseEntityMap_SetMetaData(
              caseEntityMaps: fv!item[recordType!CMGT_Case.caseEntityMap],
              initiator: ri!initiator
            )
          ),
          a!map(
            field: recordType!CMGT_Case.milestones,
            value: rule!CMGT_Milestone_SetMetaData(
              milestones: a!defaultValue(
                fv!item[recordType!CMGT_Case.milestones],
                if(
                  or(
                    a!isNullOrEmpty(
                      fv!item[recordType!CMGT_Case.caseId]
                    ),
                    a!isNullOrEmpty(
                      fv!item[recordType!CMGT_Case.folderId]
                    )
                  ),
                  rule!CMGT_WFL_Case_GenerateCaseCreationMilestones_Logic(
                    case: fv!item
