      )
    ),

  },
  fields: {
    ri!fields,
    if(
      ri!includeRoles,
      {
        recordType!CMGT_Task.roles
      },
      {}
    ),
    if(
      ri!includeMilestone,
      {
        recordType!CMGT_Task.milestone
      },
      {}
    ),
    if(
      ri!includeCase,
      {
        recordType!CMGT_Task.case
      },
      {}
    ),
    if(
      ri!includeBranches,
      {
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/3280de96-3518-4a9e-b8ae-854a84562f07"
      },
      {}
    ),
    recordType!CMGT_Task.taskBehaviorType,
    recordType!CMGT_Task.status,
    recordType!CMGT_Task.taskResponseOptions,
    recordType!CMGT_Task.selectedTaskResponseOption,
    recordType!CMGT_Task.conditionSet,
    recordType!CMGT_Task.conditionSet,
    #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
    recordType!CMGT_Task.createCaseTask,
    recordType!CMGT_Task.sendEmailTask,
    recordType!CMGT_Task.sendEmailTask,
    recordType!CMGT_Task.automationTaskAction
  },
  logicalExpressions: {
