_a-0000e9ef-12e0-8000-9bce-011c48011c48_.sail_code:
a!flatten(
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!cases,
    expression: a!localVariables(
      /*Get the public folder for the case (the one with runtime security)*/
      local!folder: rule!CMGT_UTIL_GetSubFolderByType(
        parentFolderId: fv!item[recordType!CMGT_Case.folderId],
        folderType: cons!CMGT_TXT_CASE_SUB_FOLDERS[2]
      ),
      /*Get the current security*/
      local!folderSecurity: rule!CMGT_PLUGIN_ReturnContentRoleMap(folder: local!folder),
      local!currentEditorUsers: touser(
        a!flatten(local!folderSecurity.editorUsers)
      ),
      /*Determine all users who should be in the security role map*/
      /* 1) All roles for the case - roles will never be null*/
      local!userRolesForCase: fv!item[recordType!CMGT_Case.roles.username],
      local!usersWhoAreNotInternal: touser(
        reject(
          #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
            username: _,
            groups: cons!CMGT_GRP_INTERNAL_USERS
          ),
          local!userRolesForCase
        )
      ),
      if(
        and(
          a!isNullOrEmpty(local!usersWhoAreNotInternal),
          a!isNullOrEmpty(local!currentEditorUsers)
        ),
        {},
        a!localVariables(
          /*Calculate the diff between current and desired security role map*/
          local!userEditorsToAddToFolder: difference(
            local!usersWhoAreNotInternal,
            local!currentEditorUsers
          ),
          local!usersToRemoveFromFolder: difference(
            local!currentEditorUsers,
            local!usersWhoAreNotInternal
          ),
          /*Create an output of modifications to make, if there are some to make*/
          if(
            and(
              a!isNullOrEmpty(local!userEditorsToAddToFolder),
              a!isNullOrEmpty(local!usersToRemoveFromFolder)
            ),
            {},
            {
              a!map(
                caseId: fv!item[recordType!CMGT_Case.caseId],
                folder: local!folder,
                editors: local!userEditorsToAddToFolder,
                removes: local!usersToRemoveFromFolder,
                admins: {},
                viewers: {}
              )
            }
          )
        )
      )
    )
  )
)

_a-0000e9fa-aa45-8000-9bda-011c48011c48_.sail_code:
a!flatten(
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!cases,
    expression: a!localVariables(
      local!oldCase: rule!CMGT_QR_GetCase(
        returnType: "SINGLE_OBJECT",
        caseId: fv!item[recordType!CMGT_Case.caseId],
        fields: {
          recordType!CMGT_Case.status,
          recordType!CMGT_Case.priority
        }
      ),
      local!newCase: fv!item,
      local!events: rule!CMGT_UTIL_RejectBlank(
        array: {
          if(
            fv!item[recordType!CMGT_Case.priorityId] = local!oldCase[recordType!CMGT_Case.priorityId],
            null,
            cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_PRIORITY
          ),
          if(
            fv!item[recordType!CMGT_Case.statusId] = local!oldCase[recordType!CMGT_Case.statusId],
            null,
            if(
              fv!item[recordType!CMGT_Case.statusId] = cons!CMGT_REFID_STATUS_CASE_CLOSED,
              cons!CMGT_REFID_EVENT_TYPE_CASE_CLOSED,
              cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_STATUS
            )
          ),
          if(
            fv!item[recordType!CMGT_Case.dueOn] = local!oldCase[recordType!CMGT_Case.dueOn],
            null,
            cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_DUE_DATE
          ),
          if(
            fv!item[recordType!CMGT_Case.title] = local!oldCase[recordType!CMGT_Case.title],
            null,
            cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_CASE_TITLE
          )
        }
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!events,
        expression: rule!CMGT_CaseEventHistory_Constructor(
          caseId: local!newCase[recordType!CMGT_Case.caseId],
          initiator: ri!initiator,
          eventTypeId: fv!item,
          timestamp: now(),
          details: a!match(
            value: fv!item,
            equals: cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_STATUS,
            then: a!map(
              statusId: tointeger(
                local!newCase[recordType!CMGT_Case.statusId]
              )
            ),
            equals: cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_PRIORITY,
            then: a!map(
              priorityId: tointeger(
                local!newCase[recordType!CMGT_Case.priorityId]
              )
            ),
            equals: cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_CASE_TITLE,
            then: a!map(
              title: tostring(
                local!newCase[recordType!CMGT_Case.title]
              )
            ),
            equals: cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE_DUE_DATE,
            then: a!map(
              dueOn: local!newCase[recordType!CMGT_Case.dueOn]
            ),
            default: null
          )
        )
      )
    )
  )
)

