/*Only one not listed is tasks because they have separate sub processes that include audit capture*/
#"SYSTEM_SYSRULES_forEach"(
  items: ri!cases,
  expression: #"urn:appian:function:v1:a:update"(
    fv!item,
    recordType!CMGT_Case.eventHistory,
    a!flatten(
      {
        /*EVENTS THAT ARE PROCESS-SPECIFIC (based on "eventType" input)*/
        a!match(
          value: ri!eventType,
          equals: "CREATE",
          equals: "UPDATE",
          then: rule!CMGT_CaseEventHistory_Constructor(
            caseId: fv!item[recordType!CMGT_Case.caseId],
            initiator: ri!initiator,
            eventTypeId: if(
              fv!value = "CREATE",
              cons!CMGT_REFID_EVENT_TYPE_CASE_CREATE,
              cons!CMGT_REFID_EVENT_TYPE_CASE_UPDATE
            )
          ),
          equals: "UPDATE_PROPERTY",
          then: rule!CMGT_Case_UpdateProperty_SetEvents(cases: fv!item, initiator: ri!initiator),
          equals: "MODIFY_WORKFLOW",
          then: rule!CMGT_CaseEventHistory_Constructor(
            caseId: fv!item[recordType!CMGT_Case.caseId],
            initiator: ri!initiator,
            eventTypeId: cons!CMGT_REFID_EVENT_TYPE_MODIFY_WORKFLOW
          ),
          default: {}
        ),
        /*EVENTS THAT ARE NON-PROCESS-SPECIFIC (based on "what children are populated")*/
        /* - Roles*/
        rule!CMGT_Role_SetEvents(
          roles: fv!item[recordType!CMGT_Case.roles],
          initiator: ri!initiator,
          context: "CASE"
        ),
        /* - Comments*/
        rule!CMGT_Comment_SetEvents(
          comments: fv!item[recordType!CMGT_Case.comments],
          initiator: ri!initiator,
          context: "CASE"
        ),
        /* - Documents*/
        if(
          rule!CMGT_UTIL_IsBlank(
            fv!item[recordType!CMGT_Case.documents]
          ),
          {},
          rule!CMGT_Document_SetEvents(
            documents: fv!item[recordType!CMGT_Case.documents],
            initiator: ri!initiator
          )
        ),
        /* - Tags*/
        if(
          rule!CMGT_UTIL_IsBlank(
            fv!item[recordType!CMGT_Case.tagMap]
          ),
          {},
          #"SYSTEM_SYSRULES_forEach"(
            items: fv!item[recordType!CMGT_Case.tagMap],
            expression: rule!CMGT_CaseEventHistory_Constructor(
              caseId: fv!item[recordType!CMGT_CaseTagMap.caseId],
              initiator: ri!initiator,
              eventTypeId: if(
                fv!item[recordType!CMGT_CaseTagMap.isActive],
                cons!CMGT_REFID_EVENT_TYPE_CASE_ADD_TAG,
                cons!CMGT_REFID_EVENT_TYPE_CASE_REMOVE_TAG
              ),
              details: a!map(
                tagId: fv!item[recordType!CMGT_CaseTagMap.tagId]
              )
            )
          )
        ),
      /* - Related Cases*/
        if(
          rule!CMGT_UTIL_IsBlank(
            fv!item[recordType!CMGT_Case.relatedCaseMap]
          ),
          {},
          rule!CMGT_RelatedCaseMap_SetEvents(
            relatedCaseMaps: fv!item[recordType!CMGT_Case.relatedCaseMap],
            initiator: rule!CMGT_formatEventInitiator(
              initiator: ri!initiator
            )
          )
          ),
          /* - Case Entity Map*/
          if(
            rule!CMGT_UTIL_IsBlank(
              fv!item[recordType!CMGT_Case.caseEntityMap]
            ),
            {},
            rule!CMGT_CaseEntityMap_SetEvents(
              caseEntityMaps: fv!item[recordType!CMGT_Case.caseEntityMap],
              initiator: rule!CMGT_formatEventInitiator(
                initiator: ri!initiator
              ),
              context: "CASE"
            )
          )
      }
    )
  )
)

