a!localVariables(
  /*Chosen return type*/
  local!returnType: a!defaultValue(ri!returnType, "OBJECT_ARRAY"),
  /*Determine the query parameters to use by the return type*/
  local!queryParams: a!match(
    value: local!returnType,
    equals: "OBJECT_ARRAY",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(ri!recordType),
        'type!{http://www.appian.com/ae/types/2009}Map?list'
      ),
      fetchTotalCount: false,
      isArray: true,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          cons!CMGT_INT_BATCH_SIZE_MAX
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "SINGLE_OBJECT",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        ri!recordType,
        'type!{http://www.appian.com/ae/types/2009}Map'
      ),
      fetchTotalCount: false,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "DATA_SUBSET",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}DataSubset',
      fetchTotalCount: true,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: a!defaultValue(ri!pagingInfo.startIndex, 1),
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          cons!CMGT_INT_BATCH_SIZE_MAX
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "TOTAL_COUNT",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}Integer',
      fetchTotalCount: true,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 0)
    ),
    default: fn!error(
      "Invalid returnType, must be one of: OBJECT_ARRAY, SINGLE_OBJECT, DATA_SUBSET, TOTAL_COUNT"
    )
  ),
  if(
    /*Early exit for skip execution*/
    ri!executeWhen = false,
    cast(
      local!queryParams.castToType,
      if(local!queryParams.isArray, {}, null)
    ),
    a!localVariables(
      /*Run the query*/
      local!queryResult: a!refreshVariable(
        value: #"SYSTEM_SYSRULES_queryRecordType_v2"(
          recordType: ri!recordType,
          fields: if(
            a!isNullOrEmpty(ri!groupByFields),
            reject(
              a!isNullOrEmpty,
              a!flatten(
                {
                  ri!fields,
                  index(ri!relatedRecordData, "relationship", {})
                }
              )
            ),
            #"SYSTEM_SYSRULES_aggregationFields"(
              groupings: #"SYSTEM_SYSRULES_forEach"(
                items: ri!groupByFields,
                expression: #"SYSTEM_SYSRULES_grouping"(field: fv!item, alias: tostring(fv!item))
              ),
              measures: #"SYSTEM_SYSRULES_measure"(
                field: ri!groupByFields[1],
                function: "COUNT",
                alias: "count"
              )
            )
          ),
          filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            ignoreFiltersWithEmptyValues: true,
            logicalExpressions: {
              ri!logicalExpressions,
              if(
                and(
                  a!isNotNullOrEmpty(ri!searchFields),
                  a!isNotNullOrEmpty(ri!searchTerm)
                ),
                #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  operator: "AND",
                  logicalExpressions: #"SYSTEM_SYSRULES_forEach"(
                    items: split(trim(ri!searchTerm), " "),
                    expression: a!localVariables(
                      local!splitSearchTerm: fv!item,
                      #"SYSTEM_SYSRULES_queryLogicalExpression"(
                        operator: "OR",
                        filters: #"SYSTEM_SYSRULES_forEach"(
                          items: ri!searchFields,
                          expression: #"SYSTEM_SYSRULES_queryFilter"(
                            field: fv!item,
                            operator: "includes",
                            value: local!splitSearchTerm
                          )
                        )
                      )
                    )
                  )
                ),
                {}
              )
            },
            filters: ri!filters
          ),
          pagingInfo: if(
            a!isNullOrEmpty(ri!groupByFields),
            local!queryParams.pagingInfo,
            #"urn:appian:function:v1:a:update"(
              local!queryParams.pagingInfo,
              "sort",
              null
            )
          ),
          fetchTotalCount: local!queryParams.fetchTotalCount,
          relatedRecordData: if(
            a!isNullOrEmpty(ri!groupByFields),
            ri!relatedRecordData,
            {}
          )
        ),
        refreshOnVarChange: ri!triggerRefresh,
        refreshAfter: { "RECORD_ACTION" }
      ),
      /*Format the output*/
      local!output: a!match(
        value: local!returnType,
        equals: "OBJECT_ARRAY",
        then: cast(
          local!queryParams.castToType,
          local!queryResult.data
        ),
        equals: "SINGLE_OBJECT",
        then: cast(
          local!queryParams.castToType,
          local!queryResult.data
        ),
        equals: "DATA_SUBSET",
        then: cast(
          local!queryParams.castToType,
          local!queryResult
        ),
        equals: "TOTAL_COUNT",
        then: local!queryResult.totalCount,
        default: null
      ),
      local!output
    )
  )
)

