#"SYSTEM_SYSRULES_forEach"(
  items: ri!comments,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    /*Always set these fields with each update*/
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_Comment.modifiedBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_Comment.modifiedOn,
        value: now()
      ),
      /*Set hasBeenEdited if changed and not previously set*/
      a!map(
        field: recordType!CMGT_Comment.hasBeenEdited,
        value: if(
          fv!item[recordType!CMGT_Comment.hasBeenEdited],
          true,
          a!isNullOrEmpty(
            fv!item[recordType!CMGT_Comment.commentId]
          ),
          false,
          fv!item[recordType!CMGT_Comment.commentText] <> rule!CMGT_QR_GetComment(
            commentId: fv!item[recordType!CMGT_Comment.commentId],
            fields: recordType!CMGT_Comment.commentText,
            returnType: "SINGLE_OBJECT"
          )[recordType!CMGT_Comment.commentText]
        )
      )
    },
    /*Only modify these fields if they are currently null (default value)*/
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_Comment.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_Comment.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_Comment.isActive,
        value: true
      ),
      a!map(
        field: recordType!CMGT_Comment.isPrivate,
        value: #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
          ri!initiator,
          cons!CMGT_GRP_INTERNAL_USERS
        )
      ),
      a!map(
        field: recordType!CMGT_Comment.isBookmarked,
        value: false
      ),
      /*Set caseId/taskId in case it's on the object and not the FK field*/
      a!map(
        field: recordType!CMGT_Comment.caseId,
        value: fv!item[recordType!CMGT_Comment.case.caseId]
      ),
      a!map(
        field: recordType!CMGT_Comment.taskId,
        value: fv!item[recordType!CMGT_Comment.task.taskId]
      )
    },
    /*Set metadata for all child relationships*/
    childRelationships: {
      a!map(
        field: recordType!CMGT_Comment.userTags,
        value: rule!CMGT_CommentUserTag_SetMetaData(
          commentUserTags: fv!item[recordType!CMGT_Comment.userTags]
        )
      ),
      a!map(
        field: recordType!CMGT_Comment.commentDocumentMap,
        value: rule!CMGT_CommentDocumentMap_SetMetaData(
          commentDocumentMaps: fv!item[recordType!CMGT_Comment.commentDocumentMap]
        )
      )
    }
  )
)

