rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_Comment,
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  filters: {
    ri!additionalFilters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Comment.caseId,
      operator: "=",
      value: ri!caseId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Comment.taskId,
      operator: "=",
      value: ri!taskId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Comment.commentId,
      operator: "in",
      value: ri!commentId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Comment.parentCommentId,
      operator: "is null",
      applyWhen: ri!onlyParentComments = true
    )
  },
  logicalExpressions: ri!additionalLogicalExpressions,
  fields: {
    ri!fields,
    if(
      ri!includeCase,
      recordType!CMGT_Comment.case,
      {}
    ),
    if(
      ri!includeTask,
      recordType!CMGT_Comment.task,
      {}
    ),
    if(
      ri!includeUserTagsAndDocumentMaps,
      {
        recordType!CMGT_Comment.userTags,
        recordType!CMGT_Comment.userTags,
        recordType!CMGT_Comment.commentDocumentMap,
        recordType!CMGT_Comment.commentDocumentMap
      },
      {}
    ),
    if(
      ri!includeChildComments,
      {
        recordType!CMGT_Comment.childComments,
        if(
          ri!includeUserTagsAndDocumentMaps,
          {
            recordType!CMGT_Comment.childComments,
            #"urn:appian:record-relationship:v1:e57250b0-f216-4a23-afd9-39e7d532d302-cmgt/3fbb8e70-35d5-460e-aee9-d05940b75a05/18e4567c-a0ea-4ba1-bcff-220a1ef13668-cmgt/e7ba3b0b-ab6f-4600-9551-ea4a0d70d530-cmgt",
            recordType!CMGT_Comment.childComments,
            #"urn:appian:record-relationship:v1:e57250b0-f216-4a23-afd9-39e7d532d302-cmgt/3fbb8e70-35d5-460e-aee9-d05940b75a05/4482fa71-8c71-40a5-98f2-773715556151/71840277-2340-479f-be2c-7d05e30ba12f"
          },
          {}
        )
      },
      {}
    ),
    recordType!CMGT_Comment.createdByUser
  },
  pagingInfo: ri!pagingInfo,
  relatedRecordData: {
    ri!relatedRecordData,
    if(
      ri!includeChildComments,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Comment.childComments,
        limit: 10,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!CMGT_Comment.createdOn,
          ascending: true
        )
      ),
      {}
    )
  },
  groupByFields: ri!groupByFields,
  searchTerm: ri!searchTerm,
  searchFields: {
    recordType!CMGT_Comment.commentText,
    #"urn:appian:record-field:v1:e57250b0-f216-4a23-afd9-39e7d532d302-cmgt/18e4567c-a0ea-4ba1-bcff-220a1ef13668-cmgt/e7ba3b0b-ab6f-4600-9551-ea4a0d70d530-cmgt/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
    if(
      ri!includeChildComments,
      {
        recordType!CMGT_Comment.childComments.commentText,
        #"urn:appian:record-field:v1:e57250b0-f216-4a23-afd9-39e7d532d302-cmgt/3fbb8e70-35d5-460e-aee9-d05940b75a05/18e4567c-a0ea-4ba1-bcff-220a1ef13668-cmgt/e7ba3b0b-ab6f-4600-9551-ea4a0d70d530-cmgt/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName"
      },
      {}
    )
  },
  sort: ri!sort
)

