#"SYSTEM_SYSRULES_forEach"(
  items: ri!caseDocumentSummaries,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    alwaysOverwrite: {
      a!map(
        field:recordType!CMGT_DOC_AI_CaseDocumentSummary.summary,
        value: rule!CMGT_UTIL_TruncateText(
          text: fv!item[recordType!CMGT_DOC_AI_CaseDocumentSummary.summary],
          maxLength: cons!CMGT_INT_MAX_CHAR_LIMIT_EXTRA_LARGE
        ),
        applyWhen: a!isNotNullOrEmpty(fv!item[recordType!CMGT_DOC_AI_CaseDocumentSummary.summary])
      ),
      a!map(
        field: recordType!CMGT_DOC_AI_CaseDocumentSummary.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_DOC_AI_CaseDocumentSummary.modifiedBy,
        value: ri!initiator
      )      
    },
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_DOC_AI_CaseDocumentSummary.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_DOC_AI_CaseDocumentSummary.createdBy,
        value: ri!initiator
      ),

      a!map(
        field: recordType!CMGT_DOC_AI_CaseDocumentSummary.isActive,
        value: true
      )
    }
  )
)

_a-0000e990-c462-8000-9ba7-011c48011c48_.sail_code:
/*
Future enhancement ideas:
1 Parameter to skip update if the field isn't present in the object
--- This would also apply to nested fields, as in, don't set a relationship field if the entire relationship object isn't there because it will instantiate it
2 Parameter to overwrite based on more enhanced logic
3 Allow list types for the `value`
4 Allow reuse to apply setting metadata for nested relationships, but using their own set metadata rule
--- e.g. CMGT_Entity_SetMetaData can be used in both the Case process and the individual Entity process
*/
if(
  a!isNullOrEmpty(ri!record),
  ri!record,
  a!localVariables(
    local!allUpdates: rule!CMGT_UTIL_RejectBlank(a!flatten(
      {
        ri!alwaysOverwrite,
        ri!childRelationships,
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!neverOverwrite,
          expression: #"urn:appian:function:v1:a:update"(
            fv!item,
            "value",
            /*Specifically using this instead of a!defaultValue() to account for the `{null}` value*/
            if(
              rule!CMGT_UTIL_IsBlank(ri!record[fv!item.field]),
              fv!item.value,
              ri!record[fv!item.field]
            )
          )
        )
      }
    )),
    local!onlyChangedUpdates: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!allUpdates,
        expression: if(
          or(
            tostring(ri!record[fv!item.field]) = tostring(fv!item.value),
            toboolean(fv!item.applyWhen) = false
          ),
          {},
          fv!item
        )
      )
    ),
    if(
      a!isNullOrEmpty(local!onlyChangedUpdates),
      ri!record,
      #"urn:appian:function:v1:a:update"(
        data: ri!record,
        index: local!onlyChangedUpdates.field,
        value: local!onlyChangedUpdates.value
      )
    )
  )
)

_a-0000e98b-cfe0-8000-9ba5-011c48011c48_.sail_code:
a!localVariables(
  /*Chosen return type*/
  local!returnType: a!defaultValue(ri!returnType, "OBJECT_ARRAY"),
  /*Determine the query parameters to use by the return type*/
  local!queryParams: a!match(
    value: local!returnType,
    equals: "OBJECT_ARRAY",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(ri!recordType),
        'type!{http://www.appian.com/ae/types/2009}Map?list'
      ),
      fetchTotalCount: false,
      isArray: true,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          cons!CMGT_INT_BATCH_SIZE_MAX
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "SINGLE_OBJECT",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        ri!recordType,
        'type!{http://www.appian.com/ae/types/2009}Map'
      ),
      fetchTotalCount: false,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "DATA_SUBSET",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}DataSubset',
      fetchTotalCount: true,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: a!defaultValue(ri!pagingInfo.startIndex, 1),
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          cons!CMGT_INT_BATCH_SIZE_MAX
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "TOTAL_COUNT",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}Integer',
      fetchTotalCount: true,
      isArray: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 0)
    ),
    default: fn!error(
      "Invalid returnType, must be one of: OBJECT_ARRAY, SINGLE_OBJECT, DATA_SUBSET, TOTAL_COUNT"
    )
  ),
  if(
    /*Early exit for skip execution*/
    ri!executeWhen = false,
    cast(
      local!queryParams.castToType,
      if(local!queryParams.isArray, {}, null)
    ),
    a!localVariables(
      /*Run the query*/
      local!queryResult: a!refreshVariable(
        value: #"SYSTEM_SYSRULES_queryRecordType_v2"(
          recordType: ri!recordType,
          fields: if(
            a!isNullOrEmpty(ri!groupByFields),
            reject(
              a!isNullOrEmpty,
              a!flatten(
                {
                  ri!fields,
                  index(ri!relatedRecordData, "relationship", {})
                }
              )
            ),
            #"SYSTEM_SYSRULES_aggregationFields"(
              groupings: #"SYSTEM_SYSRULES_forEach"(
                items: ri!groupByFields,
                expression: #"SYSTEM_SYSRULES_grouping"(field: fv!item, alias: tostring(fv!item))
              ),
              measures: #"SYSTEM_SYSRULES_measure"(
                field: ri!groupByFields[1],
                function: "COUNT",
                alias: "count"
              )
            )
          ),
          filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            ignoreFiltersWithEmptyValues: true,
            logicalExpressions: {
              ri!logicalExpressions,
              if(
                and(
                  a!isNotNullOrEmpty(ri!searchFields),
                  a!isNotNullOrEmpty(ri!searchTerm)
                ),
                #"SYSTEM_SYSRULES_queryLogicalExpression"(
                  operator: "AND",
                  logicalExpressions: #"SYSTEM_SYSRULES_forEach"(
                    items: split(trim(ri!searchTerm), " "),
                    expression: a!localVariables(
                      local!splitSearchTerm: fv!item,
                      #"SYSTEM_SYSRULES_queryLogicalExpression"(
                        operator: "OR",
                        filters: #"SYSTEM_SYSRULES_forEach"(
                          items: ri!searchFields,
                          expression: #"SYSTEM_SYSRULES_queryFilter"(
                            field: fv!item,
                            operator: "includes",
                            value: local!splitSearchTerm
                          )
                        )
                      )
                    )
                  )
                ),
                {}
              )
            },
            filters: ri!filters
          ),
          pagingInfo: if(
            a!isNullOrEmpty(ri!groupByFields),
            local!queryParams.pagingInfo,
            #"urn:appian:function:v1:a:update"(
              local!queryParams.pagingInfo,
              "sort",
              null
            )
          ),
          fetchTotalCount: local!queryParams.fetchTotalCount,
          relatedRecordData: if(
            a!isNullOrEmpty(ri!groupByFields),
            ri!relatedRecordData,
            {}
          )
        ),
        refreshOnVarChange: ri!triggerRefresh,
        refreshAfter: { "RECORD_ACTION" }
      ),
      /*Format the output*/
      local!output: a!match(
        value: local!returnType,
        equals: "OBJECT_ARRAY",
        then: cast(
          local!queryParams.castToType,
          local!queryResult.data
        ),
        equals: "SINGLE_OBJECT",
        then: cast(
          local!queryParams.castToType,
          local!queryResult.data
        ),
        equals: "DATA_SUBSET",
        then: cast(
          local!queryParams.castToType,
          local!queryResult
        ),
        equals: "TOTAL_COUNT",
        then: local!queryResult.totalCount,
        default: null
      ),
      local!output
    )
  )
)

