  ri!text,
  if(
    len(ri!text) > ri!maxLength,
    left(
      ri!text,
      max(
        search(" ", ri!text, ri!maxLength + 1) - 1,
        ri!maxLength
      )
    ) & "...",
    ri!text
  )
)

