/*This rule gets all threads locked for 4 hours or more*/
rule!CMGT_EML_TRI_QR_GetThreads(
  returnType: "OBJECT_ARRAY",
  threadStatuses: {
    cons!CMGT_EML_TRI_TRIAGE_STATUS_IN_PROGRESS,
    cons!CMGT_EML_TRI_TRIAGE_STATUS_PROCESSING
  },
  additionalLogicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    filters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_EML_TRI_Thread.owner,
        operator: "not null"
      ),
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_EML_TRI_Thread.modifiedOn,
        operator: "<=",
        value: a!subtractDateTime(startDateTime: now(), hours: 4)
      )
    }
  )
)

