      equals: cons!CMGT_EXT_PROCESS_ACTION_CLEAR,
      then: rule!CMGT_EXT_GetClearProcessCompletionPercentage(
        completedProcess: local!completedProcess,
        datasetUuids: ri!datasetUuids,
        triggerRefresh: ri!triggerRefresh
      ),
      equals: cons!CMGT_EXT_PROCESS_ACTION_RESET,
      then: rule!CMGT_EXT_GetResetProcessCompletionPercentage(
        completedProcess: local!completedProcess,
        datasetUuids: ri!datasetUuids
      ),
      equals: cons!CMGT_EXT_PROCESS_ACTION_RETRY_LOAD,
