a!localVariables(
  local!currentProcessData: rule!CMGT_EXT_QR_GetProcessExecutionAudit(
    processId: ri!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.processId],
    fields: {
      recordType!CMGT_EXT_ProcessExecutionAudit.startedByUser
    },
    executeWhen: a!isNotNullOrEmpty(
      ri!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.processId]
    ),
    triggerRefresh: ri!triggerRefresh,

  ),
  local!visibilityForActions: a!refreshVariable(
    value: rule!CMGT_EXT_GetActionVisibilityBasedOnLatestRun(
      processExecutionAudit: local!currentProcessData
    )
  ),
  local!datasetUuids: split(
    local!currentProcessData[recordType!CMGT_EXT_ProcessExecutionAudit.dataset],
    "; "
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    #"urn:appian:translation-string:v1:8f9eb348-269d-4699-ad42-7a2ad0f2322f"
                  },
                  size: "MEDIUM_PLUS",
                  style: "STRONG"
                )
              },
              marginBelow: "LESS",
              marginAbove: "NONE"
            )
          }
        )
      }
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: if(
            and(
              ri!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.actionCode] <> cons!CMGT_EXT_PROCESS_ACTION_RETRY_LOAD,
              index(local!visibilityForActions, "load", null),
              if(
                a!isNotNullOrEmpty(local!visibilityForActions),
                not(
                  index(
                    local!visibilityForActions,
                    "reset",
                    null
                  )
                ),
                null
              ),
              if(
                a!isNotNullOrEmpty(local!visibilityForActions),
                not(
                  index(
                    local!visibilityForActions,
                    "clear",
                    null
                  )
                ),
                null
              )
            ),
            rule!CMGT_EXT_Sub_LoadDataSet(
              brandingMap: ri!brandingMap
            ),
            rule!CMGT_EXT_Sub_CurrentDemoData(
              processExecutionAudit: ri!processExecutionAudit,
              activeMainProcess: ri!activeMainProcess,
              triggerRefresh: ri!triggerRefresh,
              visibilityForActions: local!visibilityForActions,
              datasetUuids: local!datasetUuids,
              brandingMap: ri!brandingMap
            )
          ),
          width: "4X"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!CMGT_EXT_Sub_TestUsersData(
              processExecutionAudit: ri!processExecutionAudit,
              triggerRefresh: ri!triggerRefresh,
              brandingMap: ri!brandingMap
            )
          },
          width: "3X"
        ),

      },
      marginAbove: "STANDARD"
    )
  }
)

_a-0000ec0f-47ad-8000-9ba8-011c48011c48_.sail_code:
if(
  a!defaultValue(value: ri!executeWhen, default: true()),
  a!localVariables(
    local!statuses: {
      "ACTIVE",
      "COMPLETED",
      "PAUSED",
      "CANCELLED",
      "PAUSED_BY_EXCEPTION"
    },
    local!fieldMap: {
      a!map(column: "c0", field: "processName"),
      a!map(column: "c1", field: "status"),
      a!map(column: "c2", field: "startTime"),
      a!map(column: "c3", field: "parentProcessId"),
      a!map(column: "c4", field: "processId"),
      a!map(column: "c6", field: "processModelId"),
      a!map(column: "c7", field: "caseId"),
      a!map(column: "c8", field: "processStatusCode")
    },
    #"SYSTEM_SYSRULES_forEach"(
      items: a!queryProcessAnalytics(
        report: cons!CMGT_EXT_DOC_AUDIT_PROCESS_REPORT,
        contextProcessModels: ri!contextProcessModels,
        query: #"SYSTEM_SYSRULES_query"(
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            batchSize: 50,
            sort: #"SYSTEM_SYSRULES_sortInfo"(field: "c2", ascending: false)
          ),
          logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            logicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "OR",
              filters: {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "c3",
                  operator: "=",
                  value: ri!processId,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!processId)
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: "c4",
                  operator: "=",
                  value: ri!processId,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!processId)
                )
              }
            ),
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "c1",
                operator: "in",
                value: wherecontains(ri!status, local!statuses) - 1,
                applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!status)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "c8",
                operator: "=",
                value: ri!processStatusCode,
                applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!processStatusCode)
              )
            }
          )
        )
      ).data,
      expression: reduce(
        #"urn:appian:function:v1:a:update",
        a!map(),
        merge(
          local!fieldMap.field,
          apply(
            index(fv!item, _, null()),
            local!fieldMap.column
          )
        )
      )
    )
  ),
  {}
)

_a-0000ec3d-b406-8000-9bb2-011c48011c48_.sail_code:
/*Percentage contribution by each process
Clearing of Tables - 25%
Drop studio tables  - 50%
Folder Deletion - 25%
*/
a!localVariables(
  /*Check if any cases exists with the ID's stored in demo CMGT_EXT_JSON_GENERATED_DEMO_IDS*/
  local!caseDeletionCompletionPercentage: a!refreshVariable(
    value: if(
      rule!CMGT_UTIL_IsBlank(
        rule!CMGT_QR_GetCase(
          additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_Case.caseTypeUuid,
            operator: "in",
            value: index(
              index(
                rule!CMGT_UTIL_Filter(
                  records: #"SYSTEM_SYSRULES_fromJson_v1"(cons!CMGT_EXT_JSON_GENERATED_DEMO_IDS),
                  field: "datasetUuids",
                  value: ri!datasetUuids
                ),
                "caseTypeIds",
                {}
              ),
              1,
              {}
            )
          )
        )[recordType!CMGT_Case.caseId]
      ),
      25,
      0
    ),
    refreshOnVarChange: ri!triggerRefresh
  ),
  /*Check the completion of process CMGT_EXT_Deployment*/
