/*Percentage contribution by each process
Create Users - 10%
Studio Objects Deployment - 10%
Configuration process - 20%
Case Creation - 50%
Related Cases process - 10%
*/
a!localVariables(
  /*Check the completion of process CMGT_EXT_GenerateDemoUsers*/
  local!userCreationPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[9]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  /*Check the completion of process CMGT_EXT_Deployment*/
  local!studioObjectsPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[10]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  /*Check the completion of process CMGT_EXT_Write_Configuration_Data*/
  local!configurationPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[1]
            )
          )
        }
      )
    ),
    20,
    0
  ),
  /*Check the completion of process CMGT_EXT_Create_Case and CMGT_EXT_Task_Update.
  It is sufficient to check the statsues of these 2 process where other PMs are called as the sync subprocess
  in the above PMs*/
  local!caseCreationProcess: rule!CMGT_UTIL_Filter(
    records: ri!completedProcess,
    fieldValuePairs: {
      a!map(
        field: "processModelId",
        value: tointeger(
          cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[2]
        )
      ),
      a!map(
        field: "processModelId",
        value: tointeger(
          cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[6]
        )
      )
    },
    matchingCriteria: "ANY"
  ),
  local!createdCasesIds: rule!CMGT_UTIL_RejectBlank(
    array: #"SYSTEM_SYSRULES_forEach"(
      items: rule!CMGT_UTIL_Distinct(
        value: index(
          local!caseCreationProcess,
          "caseId",
          null
        )
      ),
      expression: if(
        length(
          rule!CMGT_UTIL_Filter(
            records: local!caseCreationProcess,
            field: "caseId",
            value: fv!item
          )
        ) = 2,
        /*Since we have only 2 process related to case creation*/
        fv!item,
        null
      )
    )
  ),
  /*Here we are taking the percentage of no of cases completed for 100% then we
  are converting the value to 50%*/
  local!caseCreationPMCompletionPercentage: (
    (
      (
        length(local!createdCasesIds) / length(
          a!match(
            value: ri!datasetUuids,
            equals: cons!CMGT_EXT_TXT_GENERATED_CASE_CATEGORY_UUIDS[2],
            then: rule!CMGT_EXT_DATA_FraudulentTransactions_Case(),
            equals:cons!CMGT_EXT_TXT_GENERATED_CASE_CATEGORY_UUIDS[1],
            then: rule!CMGT_EXT_DATA_ResidentialPermit_Case(),
            default: a!flatten(
              {
                rule!CMGT_EXT_DATA_FraudulentTransactions_Case(),
                rule!CMGT_EXT_DATA_ResidentialPermit_Case()
              }
            )
          )
        )
      ) * 100
    ) * 50
  ) / 100,
  /*Check the completion of process CMGT_EXT_RelatedCaseMap_Create */
  local!relatedCasesPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS[4]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  local!completionPercentage: sum(
    local!userCreationPMCompletionPercentage,
    local!studioObjectsPMCompletionPercentage,
    local!configurationPMCompletionPercentage,
    local!caseCreationPMCompletionPercentage,
    local!relatedCasesPMCompletionPercentage
  ),
  local!completionPercentage
)

