/*Execute all the testcases before saving*/
/*The below is the structure definition of the map used in this rule*/
/*a!map(
title:Defines title of the case,
description:Defines description of the case
mapping the other values here so that the case looks same everytime it is created
 */
{
  /*Residential Permits*/
  /* Solar Permit*/
  a!map(
    title: "Commercial Solar Farm Approval",
    description: "A company proposes a large-scale solar farm to power its operations and contribute excess energy to the grid. The case involves securing permits for land use, environmental impact assessments, and addressing concerns related to visual impact and wildlife preservation.",
    caseType: "Solar Permit (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_OPEN,
    relatedCases: { "Solar Expansion in Residential Areas" }
  ),
  a!map(
    title: "Solar Expansion in Residential Areas",
    description: "A residential community seeks permits to expand their solar panel installations. The case involves addressing zoning regulations, ensuring aesthetic compatibility, and addressing concerns from neighbors regarding the impact on property values.",
    caseType: "Solar Permit (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
    relatedCases: { "Commercial Solar Farm Approval" }
  ),
  /* Addition Permit */
  a!map(
    title: "Urban Oasis Residence",
    description: "A homeowner is applying for a residential permit to make enhancements to their property in an urban setting. The proposed changes involve landscaping, interior renovations, and the addition of a home office space. The project aims to create a comfortable and functional urban oasis without incorporating solar panels or a pool.",
    caseType: "Addition Permit (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_OPEN,
    relatedCases: { "Downtown Retreat Renovation",  }
  ),
  a!map(
    title: "Downtown Retreat Renovation",
    description: "A property owner is seeking approval for a residential permit to upgrade their downtown residence. The planned improvements include landscaping modifications, interior redesign, and the establishment of a dedicated home workspace. The objective is to transform the space into a tranquil and efficient downtown retreat, with no plans for solar panels or a swimming pool.",
    caseType: "Addition Permit (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
    relatedCases: { "Urban Oasis Residence",  }
  ),

}

_a-0000ec3d-b406-8000-9bb2-011c48011c48_.sail_code:
/*Percentage contribution by each process
Drop studio tables - 10%
Folder Deletion -10%
Create Users - 10%
Studio Objects Deployment - 10%
Configuration process - 20%
Case Creation - 30%
Related Cases process - 10%

*/
a!localVariables(
  /*Check the completion of process CMGT_EXT_GenerateDemoUsers*/
  local!userCreationPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[9]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  /*number of studio objects PM complete*/
  local!numOfStudioObjectPMComplete: length(
    a!defaultValue(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[10]
            )
          )
        }
      ),
      {}
    )
  ),
  /*Check the completion of process CMGT_EXT_Deployment*/
  local!studioObjectsPMCompletionPercentage: a!match(
    value: local!numOfStudioObjectPMComplete,
    equals: 1,
    then: 10,
    equals: 2,
    then: 20,
    default: 0
  ),
  /*Check the completion of process CMGT_EXT_DeleteCaseFolders*/
  local!folderDeletionPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[11]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  /*Check the completion of process CMGT_EXT_Write_Configuration_Data*/
  local!configurationPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[1]
            )
          )
        }
      )
    ),
    20,
    0
  ),
  /*Check the completion of process CMGT_EXT_Create_Case and CMGT_EXT_Task_Update.
  It is sufficient to check the statsues of these 2 process where other PMs are called as the sync subprocess
  in the above PMs*/
  local!caseCreationProcess: rule!CMGT_UTIL_Filter(
    records: ri!completedProcess,
    fieldValuePairs: {
      a!map(
        field: "processModelId",
        value: tointeger(
          cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[2]
        )
      ),
      a!map(
        field: "processModelId",
        value: tointeger(
          cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[6]
        )
      )
    },
    matchingCriteria: "ANY"
  ),
  local!createdCasesIds: rule!CMGT_UTIL_RejectBlank(
    array: #"SYSTEM_SYSRULES_forEach"(
      items: rule!CMGT_UTIL_Distinct(
        value: index(
          local!caseCreationProcess,
          "caseId",
          null
        )
      ),
      expression: if(
        length(
          rule!CMGT_UTIL_Filter(
            records: local!caseCreationProcess,
            field: "caseId",
            value: fv!item
          )
        ) = 2,
        /*Since we have only 2 process related to case creation*/
        fv!item,
        null
      )
    )
  ),
  /*Here we are taking the percentage of no of cases completed for 100% then we
  are converting the value to 30%*/
  local!caseCreationPMCompletionPercentage: (
    (
      (
        length(local!createdCasesIds) / length(
          a!match(
            value: ri!datasetUuids,
            equals: cons!CMGT_EXT_TXT_GENERATED_CASE_CATEGORY_UUIDS[2],
            then: rule!CMGT_EXT_DATA_FraudulentTransactions_Case(),
            equals:cons!CMGT_EXT_TXT_GENERATED_CASE_CATEGORY_UUIDS[1],
            then: rule!CMGT_EXT_DATA_ResidentialPermit_Case(),
            default: a!flatten(
              {
                rule!CMGT_EXT_DATA_FraudulentTransactions_Case(),
                rule!CMGT_EXT_DATA_ResidentialPermit_Case()
              }
            )
          )
        )
      ) * 100
    ) * 30
  ) / 100,
  /*Check the completion of process CMGT_EXT_RelatedCaseMap_Create */
  local!relatedCasesPMCompletionPercentage: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!completedProcess,
        fieldValuePairs: {
          a!map(
            field: "processModelId",
            value: tointeger(
              cons!CMGT_EXT_PM_RESET_ERROR_TRACKING_PROCESS[4]
            )
          )
        }
      )
    ),
    10,
    0
  ),
  local!completionPercentage: sum(
    local!userCreationPMCompletionPercentage,
    local!studioObjectsPMCompletionPercentage,
    local!folderDeletionPMCompletionPercentage,
    local!configurationPMCompletionPercentage,
    local!caseCreationPMCompletionPercentage,
    local!relatedCasesPMCompletionPercentage
  ),
  local!completionPercentage
)

