_a-0000ec3d-b406-8000-9bb2-011c48011c48_.sail_code:
/*Execute all the testcases before saving*/
/*The below is the structure definition of the map used in this rule*/
/*a!map(
title:<Defines title of the case>,
description:<Defines description of the case>,
caseType:<Defines the casetype of the case and the value can be either "Credit Card Disputes" or "Automated Clearing House">,
status:<The value can be either cons!CMGT_REFID_STATUS_CASE_OPEN or cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS>
relatedCases:<Related Case title>

mapping the other values here so that the case looks same everytime it is created
 */
{
  /*Fraudulent Transactions*/
  /*Credit Card Disputes*/
  a!map(
    title: "Incorrect Amount Charged",
    description: "Customer reported an incorrect charge on their credit card statement for a restaurant bill. Submitted bill and transaction details. Investigation confirmed error. Account adjusted.",
    caseType: "Credit Card Disputes (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_OPEN,
    relatedCases: { "Duplicate Transaction" }
  ),
  a!map(
    title: "Duplicate Transaction",
    description: "Customer reported a duplicate charge for a single purchase on their credit card. Provided receipt and statement. Investigation confirmed error with the store. Account credited.",
    caseType: "Credit Card Disputes (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
    relatedCases: { "Incorrect Amount Charged" }
  ),
  /* Automated Clearing House*/
  a!map(
    title: "ACH Payment Not Received",
    description: "Customer reported a missing ACH payment that was not credited to the recipient's account. Provided payment details and confirmation. Investigation traced and confirmed payment failure. Issue resolved.",
    caseType: "Automated Clearing House (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_OPEN,
    relatedCases: { "ACH Transaction Error" }
  ),
  a!map(
    title: "ACH Transaction Error",
    description: "Customer reported an error in an ACH transaction. Provided transaction details and error description. Investigation confirmed the error. Transaction corrected and funds adjusted.",
    caseType: "Automated Clearing House (DEMO)",
    status: cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
    relatedCases: { "ACH Payment Not Received" }
  ),

}

