a!localVariables(
  local!brandingMap: rule!CMGT_EXT_UTIL_LoadBrandingMap(),
  local!wideScreen: {
    "TABLET_LANDSCAPE",
    "DESKTOP_NARROW",
    "DESKTOP",
    "DESKTOP_WIDE"
  },
  local!isWide: if(
    #"SYSTEM_SYSRULES_isPageWidth"(local!wideScreen),
    true,
    false
  ),
  /*only accounts for loading of data*/
  local!processExecutionAudit: a!refreshVariable(
    value: rule!CMGT_EXT_QR_GetProcessExecutionAudit(
      returnType: "SINGLE_OBJECT",
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!CMGT_EXT_ProcessExecutionAudit.auditId,
          ascending: false
        )
      )
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  local!isEmptyState: a!defaultValue(
      a!isNullOrEmpty(local!processExecutionAudit),
    true
  ),
  local!maxRefreshTime: a!refreshVariable(
    value: if(

        and(
        a!isNotNullOrEmpty(local!processExecutionAudit),
        local!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.statusCode] = cons!CMGT_EXT_PROCESS_STATUS_INPROGRESS
      ),


      a!addDateTime(startDateTime: now(), minutes: 10),
      null
    ),
    refreshOnVarChange: {
      local!processExecutionAudit,

    },
    refreshAfter: "RECORD_ACTION"
  ),
  local!activeProcessRefresh: a!refreshVariable(
    value: if(
      a!isNullOrEmpty(local!maxRefreshTime),
      null,
      now()
    ),
    refreshInterval: if(
      a!isNullOrEmpty(local!maxRefreshTime),
      null,
      if(fv!value > local!maxRefreshTime, null, 1)
    ),
    refreshOnReferencedVarChange: true,
    refreshAfter: "RECORD_ACTION"
  ),
  /*returns the id for the most recently run action*/
  local!mostRecentExecutionAudit: rule!CMGT_EXT_UTIL_SortArray(
    array: {
      local!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.auditId]
    },
    ascending: false
  ),
  local!activeMainProcess: a!refreshVariable(
    value: rule!CMGT_EXT_QPA_GetAuditProcessReportData(
      processId: index(
        {
          local!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.processId]
        },
        wherecontains(
          index(local!mostRecentExecutionAudit, 1, 1),
          {
            local!processExecutionAudit[recordType!CMGT_EXT_ProcessExecutionAudit.auditId]
          }
        ),
        0
      ),
      contextProcessModels: {
        cons!CMGT_EXT_PM_LOAD_ERROR_TRACKING_PROCESS,
        cons!CMGT_EXT_PM_CLEAR_ERROR_TRACKING_PROCESS,
        cons!CMGT_EXT_PM_RESET_DEMO_DATA
      },
      processStatusCode: cons!CMGT_EXT_PROCESS_STATUS_INPROGRESS
    ),
    refreshOnReferencedVarChange: true,
    refreshOnVarChange: local!activeProcessRefresh,
    refreshAfter: "RECORD_ACTION"
  ),
  local!triggerRefresh: a!refreshVariable(
    value: a!addDateTime(startDateTime: now(), seconds: 10),
    refreshOnVarChange: {

      local!processExecutionAudit
    },
    refreshInterval: if(
      rule!CMGT_UTIL_IsBlank(value: local!activeMainProcess),
      null,
      1
    ),
    refreshAfter: "RECORD_ACTION"
  ),
  #"SYSTEM_SYSRULES_headerContentLayout"(
    header: {},
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              if(
                local!isEmptyState,
                rule!CMGT_EXT_Sub_DemoData_EmptyState(
                  brandingMap: local!brandingMap
                ),
                rule!CMGT_EXT_Sub_DemoData_NonEmptyState(
                  processExecutionAudit: local!processExecutionAudit,
                  triggerRefresh: local!triggerRefresh,
                  activeMainProcess: local!activeMainProcess,
                  brandingMap: local!brandingMap
                )
              ),

              #"SYSTEM_SYSRULES_columnsLayout"(
                columns: {
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: rule!CMGT_EXT_Sub_DemoDataHistory(
                      triggerRefresh: local!triggerRefresh,
                      brandingMap: local!brandingMap
                    )
                  )
                },
                marginAbove: "STANDARD"
              )
            }
          )
        },
        spacing: if(local!isWide, "SPARSE", "NONE"),
        stackWhen: { "PHONE", "TABLET_PORTRAIT" },
        showDividers: true
      )
    },
    backgroundColor: local!brandingMap.DefaultBackgroundColor,
    contentsPadding: "STANDARD"
  )
)

_a-0000e98b-cfe0-8000-9ba5-011c48011c48_.sail_code:
rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_Case,
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  filters: {
    ri!additionalFilters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.caseId,
      operator: "in",
      value: ri!caseId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: ">",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate - 1,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: "<=",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    )
  },
  logicalExpressions: ri!additionalLogicalExpression,
  fields: {
    ri!fields,
    if(
      ri!excludeRelatedRecords,
      {},
      {
        recordType!CMGT_Case.status,
        recordType!CMGT_Case.priority,
        recordType!CMGT_Case.caseType
      }
    ),
    if(
      ri!includeAssignees,
      {
        recordType!CMGT_Case.roles,
        recordType!CMGT_Case.roles
      },
      {}
    ),
    if(
      ri!includePrimaryContact,
      {
        recordType!CMGT_Case.caseEntityMap,
        #"urn:appian:record-relationship:v1:d81cdd0e-c54d-4b8e-beec-b7ae37fefe7f/9455b8a8-142d-4976-8b8d-83403eedad6a/043457e2-ee0b-4c7a-8210-8671bbe73128/2a2b64c2-0530-4e0a-a173-d26a396e3cc9"
      },
      {}
    )
  },
  relatedRecordData: {
    ri!relatedRecordData,
    if(
      ri!includePrimaryContact,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.caseEntityMap,
        limit: 1,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_CaseEntityMap.entity.entityTypeId,
          operator: "=",
          value: cons!CMGT_REFID_ENTITY_TYPE_CONTACT
        )
      ),
      {}
    ),
    if(
      ri!includeAssignees,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.roles,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Role.roleTypeId,
          operator: "in",
          value: {
            cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
            cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
          }
        )
      ),
      {}
    )
  },
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  groupByFields: ri!groupByFields,
  searchTerm: ri!searchTerm,
  searchFields: ri!searchFields
)

